import React, { useState } from 'react';

const AuthRegisterForm = ({ onRegisterSuccess, onNavigateToLogin }) => {
  const [fullName, setFullName] = useState('');
  const [farmName, setFarmName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onRegisterSuccess({ fullName, farmName, email, password })) {
      alert('Registro exitoso. Ahora puedes iniciar sesión.');
      onNavigateToLogin();
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <h2 className="text-2xl font-bold text-center text-gray-800 mb-6">Regístrate</h2>
      <div>
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="fullName">
          Nombre Completo
        </label>
        <input
          type="text"
          id="fullName"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          placeholder="Tu nombre completo"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          required
        />
      </div>
      <div>
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="farmName">
          Nombre de tu Finca
        </label>
        <input
          type="text"
          id="farmName"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          placeholder="Ej. Rancho La Esperanza"
          value={farmName}
          onChange={(e) => setFarmName(e.target.value)}
          required
        />
      </div>
      <div>
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="email">
          Correo Electrónico
        </label>
        <input
          type="email"
          id="email"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          placeholder="tu@correo.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="password">
          Contraseña
        </label>
        <input
          type="password"
          id="password"
          className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          placeholder=""
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>
      <button
        type="submit"
        className="w-full bg-black text-white py-2 rounded-lg hover:bg-gray-800 transition-colors"
      >
        Registrarse
      </button>
      <p className="text-center text-gray-600 text-sm mt-4">
        ¿Ya tienes una cuenta?{' '}
        <button
          type="button"
          onClick={onNavigateToLogin}
          className="text-blue-600 hover:underline focus:outline-none"
        >
          Inicia Sesión aquí
        </button>
      </p>
    </form>
  );
};

export default AuthRegisterForm;
