import React, { useState, useEffect } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const EconomicManagementPage = ({ transactions, onAddTransaction, onDeleteTransaction, animals, employees, payslips, thirdPartyPayments, currentUser }) => {
  const [transaction, setTransaction] = useState({
    type: 'ingreso',
    category: '',
    amount: '', // Para ingresos, gastos, compras
    description: '',
    animalId: '', // Opcional
    // Campos específicos para Venta
    product: '',
    quantity: '',
    unit: '',
    pricePerUnit: '',
    client: '',
    date: new Date().toISOString().split('T')[0], // Fecha de registro/emisión de la transacción
    // Nuevos campos para ingresos con rango de fechas
    startDate: '',
    endDate: '',
  });
  const [reportPeriod, setReportPeriod] = useState('monthly'); // 'daily', 'weekly', 'monthly'
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]); // Para reporte diario
  const [selectedMonth, setSelectedMonth] = useState(new Date().toISOString().slice(0, 7)); // Para reporte mensual (YYYY-MM)
  const [selectedWeek, setSelectedWeek] = useState(getWeekNumber(new Date())); // Para reporte semanal {year, week}

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTransaction({ ...transaction, [name]: value });
  };

  const calculateTotalSale = () => {
    const qty = parseFloat(transaction.quantity);
    const price = parseFloat(transaction.pricePerUnit);
    return (qty * price).toFixed(2);
  };

  const handleSubmit = () => {
    if (!transaction.date) {
      alert('Por favor, selecciona la fecha de la transacción.');
      return;
    }
    if (transaction.type === 'ingreso' && (!transaction.startDate || !transaction.endDate)) {
      alert('Para ingresos, por favor, selecciona la fecha de inicio y fin del período.');
      return;
    }

    let finalTransaction = { ...transaction };
    if (transaction.type === 'venta') {
      finalTransaction.total = parseFloat(calculateTotalSale());
    } else {
      finalTransaction.total = parseFloat(transaction.amount);
    }

    const newId = `TRX-${Date.now().toString().slice(-5)}`;
    onAddTransaction({ ...finalTransaction, id: newId });
    alert(`Transacción de ${finalTransaction.type} registrada por $${finalTransaction.total}.`);
    // Limpiar formulario
    setTransaction({
      type: 'ingreso', category: '', amount: '', description: '',
      animalId: '', product: '', quantity: '', unit: '', pricePerUnit: '', client: '',
      date: new Date().toISOString().split('T')[0], // Resetear fecha a hoy
      startDate: '', endDate: '', // Resetear campos de fecha de período
    });
  };

  const handleGenerateInvoice = (saleData) => {
    if (!saleData || !saleData.client || !saleData.product || !saleData.quantity || !saleData.pricePerUnit) {
      alert('Por favor, completa todos los campos de la venta para generar la factura.');
      return;
    }

    const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
    const adminName = currentUser?.fullName || 'Administrador';
    const logoPlaceholder = 'https://via.placeholder.com/50x50?text=LOGO'; // Placeholder de logo

    // Crear un elemento div temporal para renderizar la plantilla del PDF
    const tempDiv = document.createElement('div');
    tempDiv.style.width = '210mm'; // Ancho A4
    tempDiv.style.padding = '15mm';
    tempDiv.style.fontFamily = 'Arial, sans-serif';
    tempDiv.style.fontSize = '10pt';
    tempDiv.style.color = '#333';
    tempDiv.style.position = 'absolute';
    tempDiv.style.left = '-9999px'; // Ocultar de la vista
    tempDiv.style.top = '-9999px';
    tempDiv.style.backgroundColor = '#fff';

    // Contenido HTML de la factura
    tempDiv.innerHTML = `
      <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 15mm; padding-bottom: 5mm; border-bottom: 1px solid #eee;">
        <img src="${logoPlaceholder}" alt="Logo Finca" style="width: 20mm; height: 20mm; object-fit: contain;" />
        <div style="text-align: right;">
          <h1 style="font-size: 18pt; margin: 0; color: #2c3e50;">${farmName}</h1>
          <p style="font-size: 10pt; margin: 0; color: #7f8c8d;">Factura de Venta</p>
        </div>
      </div>

      <h2 style="font-size: 14pt; margin-bottom: 10mm; color: #2c3e50;">Detalles de la Factura</h2>
      <p style="margin-bottom: 5px;"><strong>ID Factura:</strong> ${saleData.id || 'N/A'}</p>
      <p style="margin-bottom: 5px;"><strong>Fecha:</strong> ${saleData.date || 'N/A'}</p>
      <p style="margin-bottom: 5px;"><strong>Cliente:</strong> ${saleData.client || 'N/A'}</p>
      <div style="border-top: 1px solid #eee; border-bottom: 1px solid #eee; margin: 10mm 0; padding: 5mm 0;">
        <p style="margin-bottom: 5px;"><strong>Producto:</strong> ${saleData.product || 'N/A'}</p>
        <p style="margin-bottom: 5px;"><strong>Cantidad:</strong> ${saleData.quantity || 'N/A'} ${saleData.unit || 'N/A'}</p>
        <p style="margin-bottom: 5px;"><strong>Precio Unitario:</strong> $${parseFloat(saleData.pricePerUnit || 0).toFixed(2)}</p>
        <h2 style="text-align: right; font-size: 14pt; color: #2c3e50;">Total: $${parseFloat(saleData.total || 0).toFixed(2)}</h2>
      <div style="text-align: center; margin-top: 20mm; font-size: 9pt; color: #7f8c8d; border-top: 1px solid #eee; padding-top: 10mm;">
        <p>Generado por Campo Inteligente</p>
        <p>Administrador: ${adminName}</p>
      </div>
    `;

    document.body.appendChild(tempDiv); // Añadir al DOM temporalmente

    html2canvas(tempDiv, { scale: 2 }).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210; // A4 width in mm
      const pageHeight = 297; // A4 height in mm
      const imgHeight = canvas.height * imgWidth / canvas.width;
      let heightLeft = imgHeight;
      let position = 0;

      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }
      pdf.save(`factura_${saleData.client || 'sin_cliente'}_${saleData.id || 'sin_id'}.pdf`);
      document.body.removeChild(tempDiv); // Eliminar del DOM
    });
  };

  // --- Funciones de Utilidad de Fechas ---
  const normalizeDate = (date) => {
    const d = new Date(date);
    d.setHours(0, 0, 0, 0);
    return d;
  };

  function getWeekNumber(d) {
    d = new Date(Date.UTC(d.getFullYear(), d.getMonth(), d.getDate()));
    d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay() || 7));
    const yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
    const weekNo = Math.ceil((((d - yearStart) / 86400000) + 1) / 7);
    return { year: d.getUTCFullYear(), week: weekNo };
  }

  const getStartAndEndOfWeek = (year, week) => {
    const date = new Date(year, 0, 1 + (week - 1) * 7); // Start of the week in the given year
    const day = date.getDay(); // Day of week (0-6)
    const diff = date.getDate() - day + (day === 0 ? -6 : 1); // Adjust to Monday
    const startOfWeek = normalizeDate(new Date(date.setDate(diff)));
    const endOfWeek = normalizeDate(new Date(startOfWeek));
    endOfWeek.setDate(startOfWeek.getDate() + 6);
    return { start: startOfWeek, end: endOfWeek };
  };

  // --- Lógica de Filtrado de Reportes ---
  const filterItemsBySelectedPeriod = (items, dateProp) => {
    return items.filter(item => {
      const itemDate = normalizeDate(item[dateProp]);

      if (reportPeriod === 'daily') {
        const selectedDay = normalizeDate(selectedDate);
        return itemDate.getTime() === selectedDay.getTime();
      } else if (reportPeriod === 'weekly') {
        const { start, end } = getStartAndEndOfWeek(selectedWeek.year, selectedWeek.week);
        return itemDate >= start && itemDate <= end;
      } else if (reportPeriod === 'monthly') {
        const [year, month] = selectedMonth.split('-').map(Number);
        return itemDate.getFullYear() === year && itemDate.getMonth() === (month - 1);
      }
      return false; // No debería llegar aquí
    });
  };

  const currentPeriodTransactions = filterItemsBySelectedPeriod(transactions, 'date');
  const currentPeriodPayslips = filterItemsBySelectedPeriod(payslips, 'paymentDate');
  const currentPeriodThirdPartyPayments = filterItemsBySelectedPeriod(thirdPartyPayments, 'date'); // Filtrar pagos a terceros

  // Calcular ingresos del período
  const totalIncomePeriod = currentPeriodTransactions
    .filter(t => t.type === 'ingreso' || t.type === 'venta')
    .reduce((sum, t) => sum + t.total, 0);

  // Calcular gastos del período (compras, gastos, y nómina)
  const transactionExpensesPeriod = currentPeriodTransactions
    .filter(t => t.type === 'gasto' || t.type === 'compra')
    .reduce((sum, t) => sum + t.total, 0);

  const payslipExpensesPeriod = currentPeriodPayslips
    .reduce((sum, ps) => sum + (parseFloat(ps.netSalary) || 0), 0);

  const thirdPartyExpensesPeriod = currentPeriodThirdPartyPayments
    .reduce((sum, tpp) => sum + (parseFloat(tpp.amount) || 0), 0);

  const totalExpensesPeriod = transactionExpensesPeriod + payslipExpensesPeriod + thirdPartyExpensesPeriod;

  // Calcular ganancia neta del período
  const netProfitPeriod = totalIncomePeriod - totalExpensesPeriod;

  // --- Depuración (puedes quitar estos useEffects una vez que funcione) ---
  useEffect(() => {
    console.log(`--- Reporte ${reportPeriod} ---`);
    console.log('Transacciones filtradas:', currentPeriodTransactions);
    console.log('Fichas de pago filtradas:', currentPeriodPayslips);
    console.log('Pagos a terceros filtrados:', currentPeriodThirdPartyPayments);
    console.log('Ingresos del periodo:', totalIncomePeriod);
    console.log('Gastos de transacciones del periodo:', transactionExpensesPeriod);
    console.log('Gastos de nómina del periodo:', payslipExpensesPeriod);
    console.log('Gastos de terceros del periodo:', thirdPartyExpensesPeriod);
    console.log('Gastos Totales del periodo:', totalExpensesPeriod);
    console.log('Ganancia Neta del periodo:', netProfitPeriod);
  }, [reportPeriod, selectedDate, selectedMonth, selectedWeek, transactions, payslips, thirdPartyPayments]);

  // --- Rentabilidad por Animal (se mantiene igual, no depende del periodo de reporte) ---
  const safeEmployees = employees || [];
  const safeAnimals = animals || [];

  const totalSalaries = safeEmployees.reduce((sum, emp) => sum + (parseFloat(emp.salary) || 0), 0);
  const cowsInProductionCount = safeAnimals.filter(a => a.tipoProduccion === 'leche' && a.status === 'en_produccion').length;
  const laborCostPerCow = cowsInProductionCount > 0 ? totalSalaries / cowsInProductionCount : 0;

  const animalProfitability = safeAnimals.map(animal => {
    const animalIncome = transactions
      .filter(t => t.animalId === animal.id && t.type === 'venta')
      .reduce((sum, t) => sum + t.total, 0);

    const animalExpenses = transactions
      .filter(t => t.animalId === animal.id && (t.type === 'gasto' || t.type === 'compra'))
      .reduce((sum, t) => sum + t.total, 0);

    let proratedLaborCost = 0;
    if (animal.tipoProduccion === 'leche' && animal.status === 'en_produccion') {
      proratedLaborCost = laborCostPerCow;
    }

    const totalCost = animalExpenses + proratedLaborCost;
    const netProfitAnimal = animalIncome - totalCost;

    return {
      id: animal.id,
      name: animal.name,
      tipoProduccion: animal.tipoProduccion,
      status: animal.status,
      income: animalIncome,
      expenses: animalExpenses,
      proratedLabor: proratedLaborCost,
      totalCost: totalCost,
      netProfit: netProfitAnimal,
    };
  });

  // Información de la finca/criador para PDFs
  const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
  const adminName = currentUser?.fullName || 'Administrador';
  const logoPlaceholder = 'https://via.placeholder.com/50x50?text=LOGO'; // Placeholder de logo

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Gestión Económica</h3>

      {/* Sección de Reportes Financieros Periódicos */}
      <div className="mb-8 p-4 border rounded-lg bg-green-50">
        <h4 className="text-lg font-semibold mb-3">Reportes Financieros Periódicos</h4>
        <div className="flex justify-center mb-4 space-x-2">
          <button
            onClick={() => setReportPeriod('daily')}
            className={`px-4 py-2 rounded-lg ${reportPeriod === 'daily' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
          >
            Diario
          </button>
          <button
            onClick={() => setReportPeriod('weekly')}
            className={`px-4 py-2 rounded-lg ${reportPeriod === 'weekly' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
          >
            Semanal
          </button>
          <button
            onClick={() => setReportPeriod('monthly')}
            className={`px-4 py-2 rounded-lg ${reportPeriod === 'monthly' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
          >
            Mensual
          </button>
        </div>

        {/* Controles de selección de fecha/período */}
        <div className="mb-4 flex justify-center">
          {reportPeriod === 'daily' && (
            <input
              type="date"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            />
          )}
          {reportPeriod === 'weekly' && (
            <input
              type="week" // Input tipo week (YYYY-WNN)
              value={`${selectedWeek.year}-W${String(selectedWeek.week).padStart(2, '0')}`}
              onChange={(e) => {
                const [year, week] = e.target.value.split('-W');
                setSelectedWeek({ year: Number(year), week: Number(week) });
              }}
              className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            />
          )}
          {reportPeriod === 'monthly' && (
            <input
              type="month" // Input tipo month (YYYY-MM)
              value={selectedMonth}
              onChange={(e) => setSelectedMonth(e.target.value)}
              className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            />
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center mb-6">
          <div className="p-3 bg-green-100 rounded-lg">
            <p className="text-sm font-medium">Ingresos del Periodo</p>
            <p className="text-2xl font-bold">${totalIncomePeriod.toFixed(2)}</p>
          </div>
          <div className="p-3 bg-red-100 rounded-lg">
            <p className="text-sm font-medium">Gastos del Periodo</p>
            <p className="text-2xl font-bold">${totalExpensesPeriod.toFixed(2)}</p>
          </div>
          <div className="p-3 bg-blue-100 rounded-lg">
            <p className="text-sm font-medium">Ganancia Neta del Periodo</p>
            <p className="text-2xl font-bold">${netProfitPeriod.toFixed(2)}</p>
          </div>
        </div>
      </div>

      {/* Sección de Rentabilidad por Animal */}
      <div className="mb-8 p-4 border rounded-lg bg-yellow-50">
        <h4 className="text-lg font-semibold mb-3">Rentabilidad por Animal</h4>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border">
            <thead>
              <tr className="bg-gray-100">
                <th className="py-2 px-4 border">ID Animal</th>
                <th className="py-2 px-4 border">Nombre</th>
                <th className="py-2 px-4 border">Tipo Prod.</th>
                <th className="py-2 px-4 border">Ingresos</th>
                <th className="py-2 px-4 border">Gastos Directos</th>
                <th className="py-2 px-4 border">Mano Obra Prorrat.</th>
                <th className="py-2 px-4 border">Costo Total</th>
                <th className="py-2 px-4 border">Ganancia Neta</th>
                <th className="py-2 px-4 border">Acciones</th>
              </tr>
            </thead>
            <tbody>
              {animalProfitability.map(ap => (
                <tr key={ap.id} className="hover:bg-gray-50">
                  <td className="py-2 px-4 border">{ap.id}</td>
                  <td className="py-2 px-4 border">{ap.name}</td>
                  <td className="py-2 px-4 border">{ap.tipoProduccion}</td>
                  <td className="py-2 px-4 border">${ap.income.toFixed(2)}</td>
                  <td className="py-2 px-4 border">${ap.expenses.toFixed(2)}</td>
                  <td className="py-2 px-4 border">${ap.proratedLabor.toFixed(2)}</td>
                  <td className="py-2 px-4 border">${ap.totalCost.toFixed(2)}</td>
                  <td className={`py-2 px-4 border ${ap.netProfit >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                    ${ap.netProfit.toFixed(2)}
                  </td>
                  <td className="py-2 px-4 border">
                    {/* No hay una función de eliminación directa para la rentabilidad, ya que es un reporte */}
                    {/* Podrías añadir un botón para ver detalles del animal o sus transacciones */}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Sección de Registro de Transacciones */}
      <div className="mb-8 p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Registrar Nueva Transacción</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select
            name="type"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={transaction.type}
            onChange={handleInputChange}
          >
            <option value="ingreso">Ingreso</option>
            <option value="gasto">Gasto</option>
            <option value="compra">Compra</option>
            <option value="venta">Venta</option>
          </select>
          <input
            type="text"
            name="category"
            placeholder="Categoría (Ej: Alimento, Venta de Leche)"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={transaction.category}
            onChange={handleInputChange}
          />

          {transaction.type !== 'venta' && (
            <input
              type="number"
              name="amount"
              placeholder="Monto"
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
              value={transaction.amount}
              onChange={handleInputChange}
            />
          )}

          <input
            type="text"
            name="description"
            placeholder="Descripción Detallada"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={transaction.description}
            onChange={handleInputChange}
          />
          <select
            name="animalId"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={transaction.animalId}
            onChange={handleInputChange}
          >
            <option value="">Asociar a Animal (Opcional)</option>
            {safeAnimals.map(animal => (
              <option key={animal.id} value={animal.id}>{animal.name} ({animal.id})</option>
            ))}
          </select>

          {/* Campos específicos para Venta */}
          {transaction.type === 'venta' && (
            <>
              <input
                type="text"
                name="product"
                placeholder="Producto (Ej: Leche, Carne)"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.product}
                onChange={handleInputChange}
              />
              <input
                type="number"
                name="quantity"
                placeholder="Cantidad"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.quantity}
                onChange={handleInputChange}
              />
              <input
                type="text"
                name="unit"
                placeholder="Unidad (Ej: litros, kg)"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.unit}
                onChange={handleInputChange}
              />
              <input
                type="number"
                name="pricePerUnit"
                placeholder="Precio por unidad"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.pricePerUnit}
                onChange={handleInputChange}
              />
              <input
                type="text"
                name="client"
                placeholder="Cliente"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.client}
                onChange={handleInputChange}
              />
              <div className="col-span-full p-2 bg-gray-100 rounded-lg">
                <p className="font-semibold">Total de Venta: ${calculateTotalSale()}</p>
              </div>
            </>
          )}
          {/* Campos de fecha de período para Ingreso */}
          {transaction.type === 'ingreso' && (
            <>
              <input
                type="date"
                name="startDate"
                placeholder="Fecha Inicio Período Ingreso"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.startDate}
                onChange={handleInputChange}
              />
              <input
                type="date"
                name="endDate"
                placeholder="Fecha Fin Período Ingreso"
                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                value={transaction.endDate}
                onChange={handleInputChange}
              />
            </>
          )}
          <input
            type="date"
            name="date"
            placeholder="Fecha de Transacción"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={transaction.date}
            onChange={handleInputChange}
          />
        </div>
        <button
          onClick={handleSubmit}
          className="mt-6 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors mr-2"
        >
          Registrar Transacción
        </button>
        {transaction.type === 'venta' && (
          <button
            onClick={() => handleGenerateInvoice(transaction)} // Pasar la transacción actual para generar PDF
            className="mt-6 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors"
          >
            Generar Factura
          </button>
        )}
      </div>

      {/* Historial de Transacciones */}
      <h4 className="text-lg font-semibold mb-3">Historial de Transacciones</h4>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border">
          <thead>
            <tr className="bg-gray-100">
              <th className="py-2 px-4 border">ID</th>
              <th className="py-2 px-4 border">Tipo</th>
              <th className="py-2 px-4 border">Categoría</th>
              <th className="py-2 px-4 border">Descripción</th>
              <th className="py-2 px-4 border">Monto</th>
              <th className="py-2 px-4 border">Animal ID</th>
              <th className="py-2 px-4 border">Fecha</th>
              <th className="py-2 px-4 border">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {transactions.map(trx => (
              <tr key={trx.id} className="hover:bg-gray-50">
                <td className="py-2 px-4 border">{trx.id}</td>
                <td className="py-2 px-4 border">{trx.type}</td>
                <td className="py-2 px-4 border">{trx.category}</td>
                <td className="py-2 px-4 border">{trx.description || trx.product}</td>
                <td className="py-2 px-4 border">${trx.total.toFixed(2)}</td>
                <td className="py-2 px-4 border">{trx.animalId || 'N/A'}</td>
                <td className="py-2 px-4 border">{trx.date || 'N/A'}</td>
                <td className="py-2 px-4 border">
                  {trx.type === 'venta' && (
                    <button
                      onClick={() => handleGenerateInvoice(trx)} // Pasar la transacción para generar PDF
                      className="text-blue-600 hover:underline mr-2"
                    >
                      Ver PDF
                    </button>
                  )}
                  <button
                    onClick={() => onDeleteTransaction(trx.id)}
                    className="text-red-600 hover:underline"
                  >
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EconomicManagementPage;
