import React, { useState, useEffect } from 'react';
import AuthLayout from './components/AuthLayout';
import AuthRegisterForm from './components/AuthRegisterForm';
import AuthLoginForm from './components/AuthLoginForm';
import DashboardLayout from './components/DashboardLayout';
import AnimalRegistrationPage from './components/AnimalRegistrationPage';
import SanitaryControlPage from './components/SanitaryControlPage';
import ReproductiveControlPage from './components/ReproductiveControlPage';
import InventoryPage from './components/InventoryPage';
import EconomicManagementPage from './components/EconomicManagementPage';
import PersonnelManagementPage from './components/PersonnelManagementPage';
import SubscriptionPlansPage from './components/SubscriptionPlansPage';

import { animals as initialAnimals } from './mock/animals';
import { inventoryItems as initialInventoryItems } from './mock/inventory';
import { employees as initialEmployees, mockPayslips as initialPayslips } from './mock/employees';
import { tasks as initialTasks } from './mock/tasks';
import { transactions as initialTransactions } from './mock/transactions';

const SUBSCRIPTION_PLANS = {
  "Gratuito": { maxAnimals: 10, maxEmployees: 1, maxInventoryItems: 15, price: 0, benefits: ["Registro básico", "Reportes limitados"] },
  "Basico": { maxAnimals: 18, maxEmployees: 2, maxInventoryItems: 25, price: 2.99, benefits: ["Todo lo gratuito", "Más animales y empleados", "Reportes detallados"] },
  "Standard": { maxAnimals: 28, maxEmployees: 5, maxInventoryItems: Infinity, price: 6.99, benefits: ["Todo lo básico", "Más capacidad", "Productos ilimitados"] },
  "Premium": { maxAnimals: Infinity, maxEmployees: Infinity, maxInventoryItems: Infinity, price: 24.99, benefits: ["Todo ilimitado", "Asesoría empresarial (simulado)", "Seguimiento y consultas veterinarias gratis (simulado)"] }
};

const App = () => {
  const [currentPage, setCurrentPage] = useState('login');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const loadFromLocalStorage = (key, defaultValue) => {
    try {
      const saved = localStorage.getItem(key);
      return saved ? JSON.parse(saved) : defaultValue;
    } catch (error) {
      console.error(`Error loading ${key} from localStorage:`, error);
      return defaultValue;
    }
  };

  const saveToLocalStorage = (key, value) => {
    try {
      localStorage.setItem(key, JSON.stringify(value));
    } catch (error) {
      console.error(`Error saving ${key} to localStorage:`, error);
    }
  };

  const [animals, setAnimals] = useState(() => loadFromLocalStorage('animals', initialAnimals));
  const [inventory, setInventory] = useState(() => loadFromLocalStorage('inventory', initialInventoryItems));
  const [employees, setEmployees] = useState(() => loadFromLocalStorage('employees', initialEmployees));
  const [tasks, setTasks] = useState(() => loadFromLocalStorage('tasks', initialTasks));
  const [transactions, setTransactions] = useState(() => loadFromLocalStorage('transactions', initialTransactions));
  const [payslips, setPayslips] = useState(() => loadFromLocalStorage('payslips', initialPayslips));
  const [thirdPartyPayments, setThirdPartyPayments] = useState(() => loadFromLocalStorage('thirdPartyPayments', []));
  const [registeredUsers, setRegisteredUsers] = useState(() => loadFromLocalStorage('registeredUsers', []));

  useEffect(() => { saveToLocalStorage('registeredUsers', registeredUsers); }, [registeredUsers]);
  useEffect(() => { saveToLocalStorage('animals', animals); }, [animals]);
  useEffect(() => { saveToLocalStorage('inventory', inventory); }, [inventory]);
  useEffect(() => { saveToLocalStorage('employees', employees); }, [employees]);
  useEffect(() => { saveToLocalStorage('tasks', tasks); }, [tasks]);
  useEffect(() => { saveToLocalStorage('transactions', transactions); }, [transactions]);
  useEffect(() => { saveToLocalStorage('payslips', payslips); }, [payslips]);
  useEffect(() => { saveToLocalStorage('thirdPartyPayments', thirdPartyPayments); }, [thirdPartyPayments]);

  const checkLimit = (type) => {
    if (!currentUser) return false;
    const plan = SUBSCRIPTION_PLANS[currentUser.subscriptionPlan];
    if (!plan) return false;
    switch (type) {
      case 'animal': return animals.length < plan.maxAnimals;
      case 'employee': return employees.length < plan.maxEmployees;
      case 'inventoryItem': return inventory.length < plan.maxInventoryItems;
      default: return false;
    }
  };

  const getRemaining = (type) => {
    if (!currentUser) return 0;
    const plan = SUBSCRIPTION_PLANS[currentUser.subscriptionPlan];
    if (!plan) return 0;
    switch (type) {
      case 'animal': return plan.maxAnimals === Infinity ? 'Ilimitado' : plan.maxAnimals - animals.length;
      case 'employee': return plan.maxEmployees === Infinity ? 'Ilimitado' : plan.maxEmployees - employees.length;
      case 'inventoryItem': return plan.maxInventoryItems === Infinity ? 'Ilimitado' : plan.maxInventoryItems - inventory.length;
      default: return 0;
    }
  };

  const handleRegisterAnimal = (newAnimal) => {
    if (!checkLimit('animal')) {
      alert(`Límite de animales alcanzado para tu plan ${currentUser.subscriptionPlan}.`);
      return;
    }
    setAnimals([...animals, newAnimal]);
  };

  const handleUpdateAnimalProduction = (animalId, updateData) => {
    setAnimals(prev => prev.map(animal => animal.id === animalId ? { ...animal, ...updateData } : animal));
  };

  const handleEditAnimal = (updatedAnimal) => {
    setAnimals(animals.map(animal => animal.id === updatedAnimal.id ? updatedAnimal : animal));
  };

  const handleDeleteAnimal = (id) => {
    setAnimals(animals.filter(animal => animal.id !== id));
  };

  const renderPage = () => {
    if (!isLoggedIn) {
      return (
        <AuthLayout>
          {currentPage === 'login' ? (
            <AuthLoginForm onLoginSuccess={handleLoginSuccess} onNavigateToRegister={() => setCurrentPage('register')} registeredUsers={registeredUsers} />
          ) : (
            <AuthRegisterForm onRegisterSuccess={handleRegisterUser} onNavigateToLogin={() => setCurrentPage('login')} />
          )}
        </AuthLayout>
      );
    } else {
      let content;
      switch (currentPage) {
        case 'reproductiveControl':
          content = (
            <ReproductiveControlPage
              animals={animals}
              onUpdateAnimalProduction={handleUpdateAnimalProduction}
              onRegisterAnimal={handleRegisterAnimal}
              onRegisterReproductiveEvent={(event) => console.log('Evento registrado:', event)}
            />
          );
          break;
        case 'inventory':
          content = (
            <InventoryPage
              inventoryItems={inventory}
              onAddItem={handleAddItemToInventory}
              onUpdateItemQuantity={handleUpdateInventoryQuantity}
              onExport={handleExportInventory}
              animals={animals}
              onDeleteAnimal={handleDeleteAnimal}
              onEditAnimal={handleEditAnimal}
              onDeleteInventoryItem={handleDeleteInventoryItem}
            />
          );
          break;
        default:
          content = (<div>Contenido</div>);
      }
      return <DashboardLayout onNavigate={handleNavigate}>{content}</DashboardLayout>;
    }
  };

  return renderPage();
};

export default App;
