import React, { useState } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const PersonnelManagementPage = ({ employees, tasks, onAddEmployee, onSendPayslip, onDeletePayslip, onAssignTask, onDeleteTask, thirdPartyPayments, onAddThirdPartyPayment, onDeleteThirdPartyPayment, payslips, currentUser }) => { // Añadir currentUser
  const [employeeData, setEmployeeData] = useState({
    name: '',
    email: '',
    position: '',
    schedule: '',
    salary: '',
  });
  const [payrollData, setPayrollData] = useState({
    employeeId: '',
    grossAmount: '',
    bonuses: '',
    deductions: '',
    observations: '',
    paymentDate: new Date().toISOString().split('T')[0], // Fecha de emisión del pago
    startDate: '', // Nueva: Fecha de inicio del período de pago
    endDate: '',   // Nueva: Fecha de fin del período de pago
  });
  const [thirdPartyPaymentData, setThirdPartyPaymentData] = useState({
    personName: '',
    description: '',
    amount: '',
    date: new Date().toISOString().split('T')[0],
  });
  const [taskData, setTaskData] = useState({
    employeeId: '',
    taskType: '',
    description: '',
    toolsUsed: '',
    reason: '',
    status: 'pendiente',
  });

  // Nuevo estado para controlar la visibilidad del historial de fichas de pago
  const [showPayslipHistory, setShowPayslipHistory] = useState(false);

  const handleEmployeeChange = (e) => {
    const { name, value } = e.target;
    setEmployeeData({ ...employeeData, [name]: value });
  };

  const handlePayrollChange = (e) => {
    const { name, value } = e.target;
    setPayrollData({ ...payrollData, [name]: value });
  };

  const handleThirdPartyPaymentChange = (e) => {
    const { name, value } = e.target;
    setThirdPartyPaymentData({ ...thirdPartyPaymentData, [name]: value });
  };

  const handleTaskChange = (e) => {
    const { name, value } = e.target;
    setTaskData({ ...taskData, [name]: value });
  };

  const handleAddEmployee = () => {
    const newId = `EMP-${Date.now().toString().slice(-5)}`;
    onAddEmployee({ ...employeeData, id: newId });
    alert(`Empleado ${employeeData.name} registrado.`);
    setEmployeeData({ name: '', email: '', position: '', schedule: '', salary: '' });
  };

  const calculateNetSalary = () => {
    const gross = parseFloat(payrollData.grossAmount) || 0;
    const bonuses = parseFloat(payrollData.bonuses) || 0;
    const deductions = parseFloat(payrollData.deductions) || 0;
    return (gross + bonuses - deductions).toFixed(2);
  };

  const handleSendPayslipAction = () => {
    if (!payrollData.employeeId || !payrollData.grossAmount || !payrollData.paymentDate || !payrollData.startDate || !payrollData.endDate) {
      alert('Por favor, completa todos los campos obligatorios de la ficha de pago.');

  const generatePayslipPDF = (payslip) => {
  const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
  const adminName = currentUser?.fullName || 'Administrador';
  const logoPlaceholder = 'https://via.placeholder.com/60x60.png?text=LOGO';

  const tempDiv = document.createElement('div');
  tempDiv.style.width = '210mm';
  tempDiv.style.padding = '20mm';
  tempDiv.style.fontFamily = 'Arial, sans-serif';
  tempDiv.style.fontSize = '11pt';
  tempDiv.style.color = '#333';
  tempDiv.style.position = 'absolute';
  tempDiv.style.left = '-9999px';
  tempDiv.style.top = '-9999px';
  tempDiv.style.backgroundColor = '#fff';

  const netSalary = parseFloat(payslip.netSalary || 0).toFixed(2);
  const gross = parseFloat(payslip.grossAmount || 0).toFixed(2);
  const bonuses = parseFloat(payslip.bonuses || 0).toFixed(2);
  const deductions = parseFloat(payslip.deductions || 0).toFixed(2);

  tempDiv.innerHTML = `
    <div style="border: 2px solid #276749; border-radius: 12px; padding: 20px;">
      <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 20px;">
        <img src="${logoPlaceholder}" style="width: 60px;" alt="Logo" />
        <div style="text-align: right;">
          <h1 style="margin: 0; color: #276749;">${farmName}</h1>
          <p style="margin: 0; color: #4b5563;">Ficha de Pago</p>
        </div>
      </div>

      <div style="margin-bottom: 20px;">
        <p><strong>ID Ficha:</strong> ${payslip.id || 'N/A'}</p>
        <p><strong>Empleado:</strong> ${employees.find(e => e.id === payslip.employeeId)?.name || 'Desconocido'}</p>
        <p><strong>Fecha de Pago:</strong> ${payslip.paymentDate}</p>
        <p><strong>Período:</strong> ${payslip.startDate} al ${payslip.endDate}</p>
      </div>

      <table style="width: 100%; border-collapse: collapse; margin-bottom: 20px;">
        <thead>
          <tr style="background-color: #a7f3d0; color: #000;">
            <th style="padding: 10px; border: 1px solid #ccc;">Monto Bruto</th>
            <th style="padding: 10px; border: 1px solid #ccc;">Bonificaciones</th>
            <th style="padding: 10px; border: 1px solid #ccc;">Deducciones</th>
            <th style="padding: 10px; border: 1px solid #ccc;">Salario Neto</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td style="padding: 10px; border: 1px solid #ccc;">$${gross}</td>
            <td style="padding: 10px; border: 1px solid #ccc;">$${bonuses}</td>
            <td style="padding: 10px; border: 1px solid #ccc;">$${deductions}</td>
            <td style="padding: 10px; border: 1px solid #ccc; font-weight: bold;">$${netSalary}</td>
          </tr>
        </tbody>
      </table>

      ${payslip.observations ? `<p><strong>Observaciones:</strong> ${payslip.observations}</p>` : ''}

      <div style="text-align: center; font-size: 10pt; color: #94a3b8; border-top: 1px solid #ccc; padding-top: 10px;">
        Generado por Campo Inteligente | ${adminName}
      </div>
    </div>
  `;

  document.body.appendChild(tempDiv);

  html2canvas(tempDiv, { scale: 2 }).then(canvas => {
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'mm', 'a4');
    const imgWidth = 210;
    const imgHeight = canvas.height * imgWidth / canvas.width;

    pdf.addImage(imgData, 'PNG', 0, 0, imgWidth, imgHeight);
    pdf.save(`ficha_pago_${payslip.id || 'sin_id'}.pdf`);
    document.body.removeChild(tempDiv);
  });
};

     return;
    }

    const netSalary = calculateNetSalary();
    onSendPayslip(payrollData.employeeId, { ...payrollData, netSalary });
    alert(`Ficha de pago para ${payrollData.employeeId} enviada. Salario Neto: $${netSalary}`);
    setPayrollData({ 
      employeeId: '', grossAmount: '', bonuses: '', deductions: '', observations: '',
      paymentDate: new Date().toISOString().split('T')[0],
      startDate: '', endDate: '',
    });
  };

  const handleAddThirdPartyPaymentAction = () => {
    if (!thirdPartyPaymentData.personName || !thirdPartyPaymentData.amount || !thirdPartyPaymentData.date) {
      alert('Por favor, completa todos los campos obligatorios del pago a tercero.');
      return;
    }
    onAddThirdPartyPayment(thirdPartyPaymentData);
    alert(`Pago a ${thirdPartyPaymentData.personName} registrado.`);
    setThirdPartyPaymentData({ personName: '', description: '', amount: '', date: new Date().toISOString().split('T')[0] });
  };

  const handleAssignTaskAction = () => {
    onAssignTask(taskData.employeeId, taskData);
    alert(`Tarea asignada a ${taskData.employeeId}.`);
    setTaskData({ employeeId: '', taskType: '', description: '', toolsUsed: '', reason: '', status: 'pendiente' });
  };

  // --- Función para generar PDF de Ficha de Pago o Asignación de Tarea ---
  const handleGeneratePdf = (type, data) => {
    const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
    const adminName = currentUser?.fullName || 'Administrador';
    const logoPlaceholder = 'https://via.placeholder.com/50x50?text=LOGO'; // Placeholder de logo

    // Crear un elemento div temporal para renderizar la plantilla del PDF
    const tempDiv = document.createElement('div');
    tempDiv.style.width = '210mm'; // Ancho A4
    tempDiv.style.padding = '15mm'; // Más padding para un look minimalista
    tempDiv.style.fontFamily = 'Arial, sans-serif';
    tempDiv.style.fontSize = '10pt';
    tempDiv.style.color = '#333';
    tempDiv.style.position = 'absolute';
    tempDiv.style.left = '-9999px'; // Ocultar de la vista
    tempDiv.style.top = '-9999px';
    tempDiv.style.backgroundColor = '#fff'; // Fondo blanco

    let contentHTML = '';
    if (type === 'Ficha de Pago') {
      const employee = employees.find(emp => emp.id === data.employeeId);
      contentHTML = `
        <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 15mm; padding-bottom: 5mm; border-bottom: 1px solid #eee;">
          <img src="${logoPlaceholder}" alt="Logo Finca" style="width: 20mm; height: 20mm; object-fit: contain;" />
          <div style="text-align: right;">
            <h1 style="font-size: 18pt; margin: 0; color: #2c3e50;">${farmName}</h1>
            <p style="font-size: 10pt; margin: 0; color: #7f8c8d;">Ficha de Pago</p>
          </div>
        </div>
        <h2 style="font-size: 14pt; margin-bottom: 10mm; color: #2c3e50;">Detalles de Pago</h2>
        <p style="margin-bottom: 5px;"><strong>Empleado:</strong> ${employee?.name || 'N/A'}</p>
        <p style="margin-bottom: 5px;"><strong>ID Ficha:</strong> ${data.id}</p>
        <p style="margin-bottom: 5px;"><strong>Fecha de Emisión:</strong> ${data.paymentDate}</p>
        <p style="margin-bottom: 5px;"><strong>Período de Pago:</strong> ${data.startDate} al ${data.endDate}</p>
        <div style="border-top: 1px solid #eee; border-bottom: 1px solid #eee; margin: 10mm 0; padding: 5mm 0;">
          <p style="margin-bottom: 5px;"><strong>Monto Bruto:</strong> $${parseFloat(data.grossAmount).toFixed(2)}</p>
          <p style="margin-bottom: 5px;"><strong>Bonificaciones:</strong> $${parseFloat(data.bonuses).toFixed(2)}</p>
          <p style="margin-bottom: 5px;"><strong>Deducciones:</strong> $${parseFloat(data.deductions).toFixed(2)}</p>
        </div>
        <h2 style="text-align: right; font-size: 16pt; color: #2c3e50;">Monto Neto: $${parseFloat(data.netSalary).toFixed(2)}</h2>
        <p style="margin-bottom: 5px;"><strong>Observaciones:</strong> ${data.observations || 'N/A'}</p>
        <div style="text-align: center; margin-top: 20mm; font-size: 9pt; color: #7f8c8d; border-top: 1px solid #eee; padding-top: 10mm;">
          <p>Generado por Campo Inteligente</p>
          <p>Administrador: ${adminName}</p>
        </div>
      `;
    } else if (type === 'Pago a Tercero') {
      contentHTML = `
        <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 15mm; padding-bottom: 5mm; border-bottom: 1px solid #eee;">
          <img src="${logoPlaceholder}" alt="Logo Finca" style="width: 20mm; height: 20mm; object-fit: contain;" />
          <div style="text-align: right;">
            <h1 style="font-size: 18pt; margin: 0; color: #2c3e50;">${farmName}</h1>
            <p style="font-size: 10pt; margin: 0; color: #7f8c8d;">Comprobante de Pago a Tercero</p>
          </div>
        </div>
        <h2 style="font-size: 14pt; margin-bottom: 10mm; color: #2c3e50;">Detalles del Pago</h2>
        <p style="margin-bottom: 5px;"><strong>ID Pago:</strong> ${data.id}</p>
        <p style="margin-bottom: 5px;"><strong>Fecha de Pago:</strong> ${data.date}</p>
        <p style="margin-bottom: 5px;"><strong>Persona/Empresa:</strong> ${data.personName}</p>
        <p style="margin-bottom: 5px;"><strong>Descripción del Trabajo:</strong> ${data.description}</p>
        <h2 style="text-align: right; font-size: 16pt; color: #2c3e50;">Monto: $${parseFloat(data.amount).toFixed(2)}</h2>
        <div style="text-align: center; margin-top: 20mm; font-size: 9pt; color: #7f8c8d; border-top: 1px solid #eee; padding-top: 10mm;">
          <p>Generado por Campo Inteligente</p>
          <p>Administrador: ${adminName}</p>
        </div>
      `;
    } else if (type === 'Asignación de Tarea') {
      const employee = employees.find(emp => emp.id === data.employeeId);
      contentHTML = `
        <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 15mm; padding-bottom: 5mm; border-bottom: 1px solid #eee;">
          <img src="${logoPlaceholder}" alt="Logo Finca" style="width: 20mm; height: 20mm; object-fit: contain;" />
          <div style="text-align: right;">
            <h1 style="font-size: 18pt; margin: 0; color: #2c3e50;">${farmName}</h1>
            <p style="font-size: 10pt; margin: 0; color: #7f8c8d;">Asignación de Tarea</p>
          </div>
        </div>
        <h2 style="font-size: 14pt; margin-bottom: 10mm; color: #2c3e50;">Detalles de la Tarea</h2>
        <p style="margin-bottom: 5px;"><strong>ID Tarea:</strong> ${data.id}</p>
        <p style="margin-bottom: 5px;"><strong>Empleado:</strong> ${employee?.name || 'N/A'}</p>
        <p style="margin-bottom: 5px;"><strong>Tipo de Tarea:</strong> ${data.taskType}</p>
        <p style="margin-bottom: 5px;"><strong>Descripción:</strong> ${data.description}</p>
        <p style="margin-bottom: 5px;"><strong>Herramientas:</strong> ${data.toolsUsed || 'N/A'}</p>
        <p style="margin-bottom: 5px;"><strong>Motivo:</strong> ${data.reason || 'N/A'}</p>
        <p style="margin-bottom: 5px;"><strong>Estado:</strong> ${data.status}</p>
        <div style="text-align: center; margin-top: 20mm; font-size: 9pt; color: #7f8c8d; border-top: 1px solid #eee; padding-top: 10mm;">
          <p>Generado por Campo Inteligente</p>
          <p>Administrador: ${adminName}</p>
        </div>
      `;
    }

    tempDiv.innerHTML = contentHTML;
    document.body.appendChild(tempDiv); // Añadir al DOM temporalmente

    html2canvas(tempDiv, { scale: 2 }).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210;
      const pageHeight = 297;
      const imgHeight = canvas.height * imgWidth / canvas.width;
      let heightLeft = imgHeight;
      let position = 0;

      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }
      pdf.save(`${type.toLowerCase().replace(/ /g, '_')}_${data.id || data.employeeId || data.personName}.pdf`);
      document.body.removeChild(tempDiv); // Eliminar del DOM
    });
  };

  // --- Agrupar fichas de pago por mes ---
  const groupedPayslips = payslips.reduce((acc, ps) => {
    const date = new Date(ps.paymentDate);
    const monthYear = `${date.toLocaleString('es-ES', { month: 'long' })} ${date.getFullYear()}`;
    if (!acc[monthYear]) {
      acc[monthYear] = [];
    }
    acc[monthYear].push(ps);
    return acc;
  }, {});

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Gestión de Personal Integrada</h3>

      {/* Sección de Registro de Empleados */}
      <div className="mb-8 p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Registrar Nuevo Empleado</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            name="name"
            placeholder="Nombre Completo"
            className="w-full px-4 py-2 border rounded-lg"
            value={employeeData.name}
            onChange={handleEmployeeChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Correo Electrónico"
            className="w-full px-4 py-2 border rounded-lg"
            value={employeeData.email}
            onChange={handleEmployeeChange}
          />
          <input
            type="text"
            name="position"
            placeholder="Cargo"
            className="w-full px-4 py-2 border rounded-lg"
            value={employeeData.position}
            onChange={handleEmployeeChange}
          />
          <input
            type="text"
            name="schedule"
            placeholder="Turno / Horario"
            className="w-full px-4 py-2 border rounded-lg"
            value={employeeData.schedule}
            onChange={handleEmployeeChange}
          />
          <input
            type="number"
            name="salary"
            placeholder="Salario Base"
            className="w-full px-4 py-2 border rounded-lg"
            value={employeeData.salary}
            onChange={handleEmployeeChange}
          />
        </div>
        <button
          onClick={handleAddEmployee}
          className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
        >
          Registrar Empleado
        </button>
      </div>

      {/* Sección de Fichas de Pago */}
      <div className="mb-8 p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Gestionar Fichas de Pago</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select
            name="employeeId"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.employeeId}
            onChange={handlePayrollChange}
          >
            <option value="">Selecciona Empleado</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>{emp.name}</option>
            ))}
          </select>
          <input
            type="number"
            name="grossAmount"
            placeholder="Monto Bruto"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.grossAmount}
            onChange={handlePayrollChange}
          />
          <input
            type="number"
            name="bonuses"
            placeholder="Bonificaciones"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.bonuses}
            onChange={handlePayrollChange}
          />
          <input
            type="number"
            name="deductions"
            placeholder="Deducciones"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.deductions}
            onChange={handlePayrollChange}
          />
          <input
            type="date"
            name="startDate"
            placeholder="Fecha Inicio Período"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.startDate}
            onChange={handlePayrollChange}
          />
          <input
            type="date"
            name="endDate"
            placeholder="Fecha Fin Período"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.endDate}
            onChange={handlePayrollChange}
          />
          <input
            type="date"
            name="paymentDate"
            placeholder="Fecha de Emisión del Pago"
            className="w-full px-4 py-2 border rounded-lg"
            value={payrollData.paymentDate}
            onChange={handlePayrollChange}
          />
          <textarea
            name="observations"
            placeholder="Observaciones"
            rows="2"
            className="w-full px-4 py-2 border rounded-lg col-span-full"
            value={payrollData.observations}
            onChange={handlePayrollChange}
          ></textarea>
          <div className="col-span-full p-2 bg-gray-100 rounded-lg">
            <p className="font-semibold">Monto Neto a Recibir: ${calculateNetSalary()}</p>
          </div>
        </div>
        <button
          onClick={handleSendPayslipAction}
          className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors mr-2"
        >
          Guardar y Enviar Ficha
        </button>
      </div>

      {/* Sección de Pagos a Terceros / Mano de Obra Independiente */}
      <div className="mb-8 p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Registrar Pago a Terceros / Mano de Obra</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            name="personName"
            placeholder="Nombre de la Persona/Empresa"
            className="w-full px-4 py-2 border rounded-lg"
            value={thirdPartyPaymentData.personName}
            onChange={handleThirdPartyPaymentChange}
          />
          <input
            type="number"
            name="amount"
            placeholder="Monto del Pago"
            className="w-full px-4 py-2 border rounded-lg"
            value={thirdPartyPaymentData.amount}
            onChange={handleThirdPartyPaymentChange}
          />
          <input
            type="text"
            name="description"
            placeholder="Descripción del Trabajo/Servicio"
            className="w-full px-4 py-2 border rounded-lg"
            value={thirdPartyPaymentData.description}
            onChange={handleThirdPartyPaymentChange}
          />
          <input
            type="date"
            name="date"
            placeholder="Fecha del Pago"
            className="w-full px-4 py-2 border rounded-lg"
            value={thirdPartyPaymentData.date}
            onChange={handleThirdPartyPaymentChange}
          />
        </div>
        <button
          onClick={handleAddThirdPartyPaymentAction}
          className="mt-4 bg-purple-600 text-white py-2 px-4 rounded-lg hover:bg-purple-700 transition-colors"
        >
          Registrar Pago a Tercero
        </button>
      </div>

      {/* Sección de Asignación de Tareas */}
      <div className="p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Asignar Tarea</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select
            name="employeeId"
            className="w-full px-4 py-2 border rounded-lg"
            value={taskData.employeeId}
            onChange={handleTaskChange}
          >
            <option value="">Selecciona Empleado</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>{emp.name}</option>
            ))}
          </select>
          <input
            type="text"
            name="taskType"
            placeholder="Tipo de Tarea"
            className="w-full px-4 py-2 border rounded-lg"
            value={taskData.taskType}
            onChange={handleTaskChange}
          />
          <textarea
            name="description"
            placeholder="Descripción de la tarea"
            rows="2"
            className="w-full px-4 py-2 border rounded-lg col-span-full"
            value={taskData.description}
            onChange={handleTaskChange}
          ></textarea>
          <input
            type="text"
            name="toolsUsed"
            placeholder="Herramientas utilizadas"
            className="w-full px-4 py-2 border rounded-lg"
            value={taskData.toolsUsed}
            onChange={handleTaskChange}
          />
          <input
            type="text"
            name="reason"
            placeholder="Motivo (Opcional)"
            className="w-full px-4 py-2 border rounded-lg"
            value={taskData.reason}
            onChange={handleTaskChange}
          />
          <select
            name="status"
            className="w-full px-4 py-2 border rounded-lg"
            value={taskData.status}
            onChange={handleTaskChange}
          >
            <option value="pendiente">Pendiente</option>
            <option value="en_progreso">En Progreso</option>
            <option value="completada">Completada</option>
          </select>
        </div>
        <button
          onClick={handleAssignTaskAction}
          className="mt-4 bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-colors mr-2"
        >
          Asignar y Enviar Tarea
        </button>
      </div>

      {/* Historial de Tareas Asignadas */}
      <h4 className="text-lg font-semibold mb-3 mt-8">Historial de Tareas</h4>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border">
          <thead>
            <tr className="bg-gray-100">
              <th className="py-2 px-4 border">Empleado</th>
              <th className="py-2 px-4 border">Tipo</th>
              <th className="py-2 px-4 border">Descripción</th>
              <th className="py-2 px-4 border">Estado</th>
              <th className="py-2 px-4 border">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map(task => (
              <tr key={task.id} className="hover:bg-gray-50">
                <td className="py-2 px-4 border">{employees.find(emp => emp.id === task.employeeId)?.name || 'N/A'}</td>
                <td className="py-2 px-4 border">{task.taskType}</td>
                <td className="py-2 px-4 border">{task.description}</td>
                <td className="py-2 px-4 border">{task.status}</td>
                <td className="py-2 px-4 border">
                  <button
                    onClick={() => handleViewPdf('Asignación de Tarea', task)} // Simular PDF de tarea
                    className="text-blue-600 hover:underline mr-2"
                  >
                    Ver PDF
                  </button>
                  <button
                    onClick={() => onDeleteTask(task.id)}
                    className="text-red-600 hover:underline"
                  >
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Historial de Fichas de Pago */}
      <h4 className="text-lg font-semibold mb-3 mt-8 cursor-pointer flex items-center justify-between bg-gray-100 p-2 rounded-lg" onClick={() => setShowPayslipHistory(!showPayslipHistory)}>
        Historial de Fichas de Pago
        <svg className={`w-5 h-5 transform transition-transform ${showPayslipHistory ? 'rotate-180' : ''}`} fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path></svg>
      </h4>
      {showPayslipHistory && (
        <div className="overflow-x-auto">
          {Object.keys(groupedPayslips).length === 0 ? (
            <p className="text-gray-600">No hay fichas de pago registradas.</p>
          ) : (
            Object.keys(groupedPayslips).sort((a, b) => {
              // Ordenar por año y luego por mes (descendente)
              const [monthA, yearA] = a.split(' ');
              const [monthB, yearB] = b.split(' ');
              const dateA = new Date(`${monthA} 1, ${yearA}`);
              const dateB = new Date(`${monthB} 1, ${yearB}`);
              return dateB - dateA;
            }).map(monthYear => (
              <div key={monthYear} className="mb-6">
                <h5 className="text-md font-semibold mb-2 bg-gray-100 p-2 rounded-lg">{monthYear}</h5>
                <table className="min-w-full bg-white border">
                  <thead>
                    <tr className="bg-gray-50">
                      <th className="py-2 px-4 border">ID Ficha</th>
                      <th className="py-2 px-4 border">Empleado</th>
                      <th className="py-2 px-4 border">Monto Neto</th>
                      <th className="py-2 px-4 border">Período</th>
                      <th className="py-2 px-4 border">Fecha Pago</th>
                      <th className="py-2 px-4 border">Acciones</th>
                    </tr>
                  </thead>
                  <tbody>
                    {groupedPayslips[monthYear].map(ps => (
                      <tr key={ps.id} className="hover:bg-gray-50">
                        <td className="py-2 px-4 border">{ps.id}</td>
                        <td className="py-2 px-4 border">{employees.find(emp => emp.id === ps.employeeId)?.name || 'N/A'}</td>
                        <td className="py-2 px-4 border">${(parseFloat(ps.netSalary) || 0).toFixed(2)}</td>
                        <td className="py-2 px-4 border">{ps.startDate} al {ps.endDate}</td>
                        <td className="py-2 px-4 border">{ps.paymentDate}</td>
                        <td className="py-2 px-4 border">
                          <button
                            onClick={() => handleViewPdf('Ficha de Pago', ps)}
                            className="text-blue-600 hover:underline mr-2"
                          >
                            Ver PDF
                          </button>
                          <button
                            onClick={() => onDeletePayslip(ps.id)}
                            className="text-red-600 hover:underline"
                          >
                            Eliminar
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ))
          )}
        </div>
      )}

      {/* Historial de Pagos a Terceros */}
      <h4 className="text-lg font-semibold mb-3 mt-8">Historial de Pagos a Terceros</h4>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border">
          <thead>
            <tr className="bg-gray-100">
              <th className="py-2 px-4 border">ID Pago</th>
              <th className="py-2 px-4 border">Persona/Empresa</th>
              <th className="py-2 px-4 border">Descripción</th>
              <th className="py-2 px-4 border">Monto</th>
              <th className="py-2 px-4 border">Fecha</th>
              <th className="py-2 px-4 border">Acciones</th>
            </tr>
          </thead>
          <tbody>
            {thirdPartyPayments.map(tpp => (
              <tr key={tpp.id} className="hover:bg-gray-50">
                <td className="py-2 px-4 border">{tpp.id}</td>
                <td className="py-2 px-4 border">{tpp.personName}</td>
                <td className="py-2 px-4 border">{tpp.description}</td>
                <td className="py-2 px-4 border">${(parseFloat(tpp.amount) || 0).toFixed(2)}</td>
                <td className="py-2 px-4 border">{tpp.date}</td>
                <td className="py-2 px-4 border">
                  <button
                    onClick={() => handleViewPdf('Pago a Tercero', tpp)}
                    className="text-blue-600 hover:underline mr-2"
                  >
                    Ver PDF
                  </button>
                  <button
                    onClick={() => onDeleteThirdPartyPayment(tpp.id)}
                    className="text-red-600 hover:underline"
                  >
                    Eliminar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PersonnelManagementPage;
