      {isSettingsOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-end z-50">
          <div className={`w-80 ${themeClasses.sidebarBg} p-6 ${themeClasses.text} shadow-lg overflow-y-auto`}>
