import React, { useState, useRef, useEffect } from 'react';
import { exportToCsv, exportTableToPdf } from '../utils/exportHelpers'; // Importar funciones de exportaci√≥n

const ProductionControlPage = ({ animals, onUpdateAnimalProduction, onRegisterProductionRecord, productionRecords, setProductionRecords, currentUser, logoUrl }) => {
  const [currentProductionSection, setCurrentProductionSection] = useState('general'); // 'general', 'carne', 'leche'

  const [productionData, setProductionData] = useState({
    animalId: '',
    fechaRegistro: '',
    tipoProduccion: '', // 'leche', 'carne'
    cantidad: '', // Cantidad de producci√≥n (ej. litros de leche, kg de carne)
    unidadMedida: '', // 'litros', 'kg'
    observaciones: '',
  });

  const [editingRecord, setEditingRecord] = useState(null); // To hold the record being edited
  const productionTableRef = useRef(null); // Ref for the production history table

  // Estados para el an√°lisis de tendencias y consejos de IA
  const [milkTrend, setMilkTrend] = useState('estable'); // 'aumentando', 'decayendo', 'estable'
  const [meatTrend, setMeatTrend] = useState('estable'); // 'aumentando', 'decayendo', 'estable'
  const [milkAdvice, setMilkAdvice] = useState('');
  const [meatAdvice, setMeatAdvice] = useState('');
  const [aiAdviceHistory, setAiAdviceHistory] = useState([]); // Nuevo estado para el historial de consejos

  // Funci√≥n para calcular la tendencia y generar consejos
  const analyzeProductionAndAdvise = (records, type) => {
    const relevantRecords = records
      .filter(record => record.tipoProduccion === type)
      .sort((a, b) => new Date(a.fechaRegistro) - new Date(b.fechaRegistro));

    if (relevantRecords.length < 2) {
      return { trend: 'estable', advice: 'Necesitamos m√°s datos para analizar la tendencia de producci√≥n.' };
    }

    const latestRecords = relevantRecords.slice(-3); // Tomar los √∫ltimos 3 registros para un an√°lisis simple

    if (latestRecords.length < 2) {
      return { trend: 'estable', advice: 'Necesitamos al menos dos registros para analizar la tendencia.' };
    }

    let increasingCount = 0;
    let decreasingCount = 0;

    for (let i = 1; i < latestRecords.length; i++) {
      if (latestRecords[i].cantidad > latestRecords[i - 1].cantidad) {
        increasingCount++;
      } else if (latestRecords[i].cantidad < latestRecords[i - 1].cantidad) {
        decreasingCount++;
      }
    }

    let trend = 'estable';
    let advice = '';

    if (increasingCount > decreasingCount) {
      trend = 'aumentando';
      advice = `¬°Excelente! La producci√≥n de ${type} est√° aumentando. Sigue con las buenas pr√°cticas de alimentaci√≥n y manejo.`;
    } else if (decreasingCount > increasingCount) {
      trend = 'decayendo';
      advice = `¬°Atenci√≥n! La producci√≥n de ${type} est√° decayendo. Considera revisar la dieta, el estado de salud del animal o el manejo del estr√©s.`;
    } else {
      trend = 'estable';
      advice = `La producci√≥n de ${type} se mantiene estable. Si buscas mejorarla, eval√∫a optimizar la alimentaci√≥n o el confort del animal.`;
    }

    return { trend, advice };
  };

  useEffect(() => {
    const { trend: milkT, advice: milkA } = analyzeProductionAndAdvise(productionRecords, 'leche');
    setMilkTrend(milkT);
    setMilkAdvice(milkA);
    if (milkA && milkA !== 'Necesitamos m√°s datos para analizar la tendencia de producci√≥n.' && milkA !== 'Necesitamos al menos dos registros para analizar la tendencia.') {
      setAiAdviceHistory(prev => [...prev, { date: new Date().toISOString().split('T')[0], type: 'leche', advice: milkA }]);
    }

    const { trend: meatT, advice: meatA } = analyzeProductionAndAdvise(productionRecords, 'carne');
    setMeatTrend(meatT);
    setMeatAdvice(meatA);
    if (meatA && meatA !== 'Necesitamos m√°s datos para analizar la tendencia de producci√≥n.' && meatA !== 'Necesitamos al menos dos registros para analizar la tendencia.') {
      setAiAdviceHistory(prev => [...prev, { date: new Date().toISOString().split('T')[0], type: 'carne', advice: meatA }]);
    }
  }, [productionRecords]);

  const handleProductionChange = (e) => {
    const { name, value } = e.target;
    setProductionData({ ...productionData, [name]: value });
  };

  const handleProductionSubmit = () => {
    const { animalId, fechaRegistro, tipoProduccion, cantidad, unidadMedida, observaciones } = productionData;

    if (!animalId || !fechaRegistro || !tipoProduccion || !cantidad || !unidadMedida) {
      alert('¬°Ups! Para registrar la producci√≥n, necesitamos que completes todos los campos obligatorios. ¬°No te rindas, ya casi lo logras!');
      return;
    }

    onRegisterProductionRecord({
      id: `PROD-${Date.now().toString().slice(-5)}`, // Generar ID √∫nico
      animalId,
      fechaRegistro,
      tipoProduccion,
      cantidad: parseFloat(cantidad),
      unidadMedida,
      observaciones,
    });

    alert('¬°Producci√≥n registrada con √©xito! ¬°Tus animales son unos campeones!');
    setProductionData({
      animalId: '',
      fechaRegistro: '',
      tipoProduccion: '',
      cantidad: '',
      unidadMedida: '',
      observaciones: '',
    });
  };

  const handleDeleteProductionRecord = (recordId) => {
    if (window.confirm('¬øEst√°s seguro de que quieres eliminar este registro de producci√≥n? ¬°Esta acci√≥n no se puede deshacer!')) {
      const updatedRecords = productionRecords.filter(record => record.id !== recordId);
      setProductionRecords(updatedRecords); // Update state in App.js via prop
      alert('¬°Registro de producci√≥n eliminado! Como si nunca hubiera existido.');
    }
  };

  const startEditingProductionRecord = (record) => {
    setEditingRecord({ ...record }); // Create a copy to edit
    alert('¬°A editar se ha dicho! Modifica los campos y guarda los cambios.');
  };

  const handleEditingProductionChange = (e) => {
    const { name, value } = e.target;
    setEditingRecord(prev => ({ ...prev, [name]: value }));
  };

  const saveEditedProductionRecord = () => {
    const updatedRecords = productionRecords.map(record =>
      record.id === editingRecord.id ? editingRecord : record
    );
    setProductionRecords(updatedRecords); // Update state in App.js via prop
    setEditingRecord(null); // Exit editing mode
    alert('¬°Cambios guardados! Tu historial de producci√≥n est√° al d√≠a.');
  };

  const cancelEditingProductionRecord = () => {
    setEditingRecord(null); // Exit editing mode
    alert('¬°Edici√≥n cancelada! No te preocupes, tus datos est√°n a salvo.');
  };

  const handleExportProductionHistory = async (format) => {
    if (productionRecords.length === 0) {
      alert('¬°No hay registros de producci√≥n para exportar! ¬øQu√© esperas para empezar a registrar?');
      return;
    }

    const filename = `historial_produccion_${new Date().toISOString().split('T')[0]}`;
    const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
    const adminName = currentUser?.fullName || 'Administrador';

    if (format === 'csv') {
      exportToCsv(productionRecords, filename);
      alert('¬°Exportando a CSV! Tu historial estar√° listo en un santiam√©n.');
    } else if (format === 'pdf') {
      await exportTableToPdf(productionTableRef.current, filename, 'Historial de Producci√≥n', farmName, adminName, logoUrl);
      alert('¬°Generando PDF! Tu historial se ver√° de lujo en papel.');
    }
  };

  const filteredProductionRecords = productionRecords.filter(record => {
    if (currentProductionSection === 'carne' && record.tipoProduccion !== 'carne') {
      return false;
    }
    if (currentProductionSection === 'leche' && record.tipoProduccion !== 'leche') {
      return false;
    }
    return true;
  });

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Control de Producci√≥n</h3>

      <div className="flex justify-center mb-6 space-x-4">
        <button
          onClick={() => setCurrentProductionSection('general')}
          className={`px-6 py-2 rounded-lg ${currentProductionSection === 'general' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          General
        </button>
        <button
          onClick={() => setCurrentProductionSection('leche')}
          className={`px-6 py-2 rounded-lg ${currentProductionSection === 'leche' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Producci√≥n de Leche
        </button>
        <button
          onClick={() => setCurrentProductionSection('carne')}
          className={`px-6 py-2 rounded-lg ${currentProductionSection === 'carne' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Producci√≥n de Carne
        </button>
      </div>

      {/* Secci√≥n de Tendencias y Consejos */}
      <div className="p-4 border rounded-lg bg-gray-100 mb-8">
        <h4 className="text-lg font-semibold mb-3">An√°lisis de Tendencias de Producci√≥n</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <p className="text-gray-700 font-bold">Tendencia de Leche:</p>
            <span className={`text-lg font-semibold ${milkTrend === 'aumentando' ? 'text-green-600' : milkTrend === 'decayendo' ? 'text-red-600' : 'text-gray-600'}`}>
              {milkTrend === 'aumentando' ? '¬°Aumentando! üìà' : milkTrend === 'decayendo' ? '¬°Decayendo! üìâ' : 'Estable ‚ÜîÔ∏è'}
            </span>
            <p className="text-sm text-gray-600 mt-1">{milkAdvice}</p>
          </div>
          <div>
            <p className="text-gray-700 font-bold">Tendencia de Carne:</p>
            <span className={`text-lg font-semibold ${meatTrend === 'aumentando' ? 'text-green-600' : meatTrend === 'decayendo' ? 'text-red-600' : 'text-gray-600'}`}>
              {meatTrend === 'aumentando' ? '¬°Aumentando! üìà' : meatTrend === 'decayendo' ? '¬°Decayendo! üìâ' : 'Estable ‚ÜîÔ∏è'}
            </span>
            <p className="text-sm text-gray-600 mt-1">{meatAdvice}</p>
          </div>
        </div>
      </div>

      {/* Historial de Consejos de la IA */}
      <div className="p-4 border rounded-lg bg-blue-50 mb-8">
        <h4 className="text-lg font-semibold mb-3">Historial de Consejos de la IA</h4>
        {aiAdviceHistory.length === 0 ? (
          <p className="text-gray-600">A√∫n no hay consejos de la IA. ¬°Registra m√°s producci√≥n para obtenerlos!</p>
        ) : (
          <div className="max-h-48 overflow-y-auto">
            {aiAdviceHistory.map((advice, index) => (
              <div key={index} className="mb-2 p-2 bg-blue-100 rounded-lg">
                <p className="text-sm text-gray-800">
                  <span className="font-semibold">{advice.date} - {advice.type.charAt(0).toUpperCase() + advice.type.slice(1)}:</span> {advice.advice}
                </p>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Formulario de Registro de Producci√≥n */}
      <div className="p-4 border rounded-lg bg-gray-50 mb-8">
        <h4 className="text-lg font-semibold mb-3">Registrar Nueva Producci√≥n</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <label className="block text-gray-700 text-sm font-bold mb-1">Animal:</label>
          <select
            name="animalId"
            className="w-full px-4 py-2 border rounded-lg"
            value={productionData.animalId}
            onChange={handleProductionChange}
          >
            <option value="">Selecciona Animal</option>
            {animals.map(animal => (
              <option key={animal.id} value={animal.id}>{animal.name} ({animal.id})</option>
            ))}
          </select>
          <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Registro:</label>
          <input
            type="date"
            name="fechaRegistro"
            placeholder="Fecha de Registro"
            className="w-full px-4 py-2 border rounded-lg"
            value={productionData.fechaRegistro}
            onChange={handleProductionChange}
          />
          <label className="block text-gray-700 text-sm font-bold mb-1">Tipo de Producci√≥n:</label>
          <select
            name="tipoProduccion"
            className="w-full px-4 py-2 border rounded-lg"
            value={productionData.tipoProduccion}
            onChange={handleProductionChange}
          >
            <option value="">Selecciona Tipo</option>
            <option value="leche">Leche</option>
            <option value="carne">Carne</option>
          </select>
          <label className="block text-gray-700 text-sm font-bold mb-1">Cantidad:</label>
          <input
            type="number"
            name="cantidad"
            placeholder="Cantidad"
            className="w-full px-4 py-2 border rounded-lg"
            value={productionData.cantidad}
            onChange={handleProductionChange}
          />
          <label className="block text-gray-700 text-sm font-bold mb-1">Unidad de Medida:</label>
          <input
            type="text"
            name="unidadMedida"
            placeholder="Unidad de Medida (ej. litros, kg)"
            className="w-full px-4 py-2 border rounded-lg"
            value={productionData.unidadMedida}
            onChange={handleProductionChange}
          />
          <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones:</label>
          <textarea
            name="observaciones"
            placeholder="Observaciones"
            rows="2"
            className="w-full px-4 py-2 border rounded-lg col-span-full"
            value={productionData.observaciones}
            onChange={handleProductionChange}
          ></textarea>
        </div>
        <button
          onClick={handleProductionSubmit}
          className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
        >
          Registrar Producci√≥n
        </button>
      </div>

      {/* Historial de Producci√≥n */}
      <div className="p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Historial de Producci√≥n</h4>
        <div className="flex justify-end mb-4">
          <button
            onClick={() => handleExportProductionHistory('csv')}
            className="bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-colors mr-2"
          >
            Exportar a CSV
          </button>
          <button
            onClick={() => handleExportProductionHistory('pdf')}
            className="bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700 transition-colors"
          >
            Exportar a PDF
          </button>
        </div>
        <div className="overflow-x-auto rounded-lg shadow-md">
          <table ref={productionTableRef} className="min-w-full bg-white text-sm">
            <thead className="bg-gray-800 text-white">
              <tr>
                <th className="py-3 px-4 text-left font-semibold rounded-tl-lg">ID Registro</th>
                <th className="py-3 px-4 text-left font-semibold">ID Animal</th>
                <th className="py-3 px-4 text-left font-semibold">Fecha</th>
                <th className="py-3 px-4 text-left font-semibold">Tipo</th>
                <th className="py-3 px-4 text-left font-semibold">Cantidad</th>
                <th className="py-3 px-4 text-left font-semibold">Unidad</th>
                <th className="py-3 px-4 text-left font-semibold">Observaciones</th>
                <th className="py-3 px-4 text-left font-semibold rounded-tr-lg">Acciones</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {filteredProductionRecords.map((record) => (
                <tr key={record.id} className="hover:bg-gray-100 transition-colors">
                  <td className="py-3 px-4">{record.id}</td>
                  <td className="py-3 px-4">{record.animalId}</td>
                  <td className="py-3 px-4">{record.fechaRegistro}</td>
                  <td className="py-3 px-4">{record.tipoProduccion}</td>
                  <td className="py-3 px-4">{record.cantidad}</td>
                  <td className="py-3 px-4">{record.unidadMedida}</td>
                  <td className="py-3 px-4">{record.observaciones}</td>
                  <td className="py-3 px-4">
                    <button
                      onClick={() => startEditingProductionRecord(record)}
                      className="text-blue-600 hover:text-blue-800 font-medium mr-2 transition-colors"
                    >
                      Editar
                    </button>
                    <button
                      onClick={() => handleDeleteProductionRecord(record.id)}
                      className="text-red-600 hover:text-red-800 font-medium transition-colors"
                    >
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Edit Form for Production Record */}
        {editingRecord && (
          <div className="mt-8 p-6 border rounded-lg bg-yellow-50 shadow-lg">
            <h4 className="text-lg font-semibold mb-4 text-gray-800">Editar Registro de Producci√≥n: {editingRecord.id}</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-1">ID Animal:</label>
                <input
                  type="text"
                  name="animalId"
                  className="w-full px-4 py-2 border rounded-lg bg-gray-200 cursor-not-allowed"
                  value={editingRecord.animalId}
                  readOnly
                />
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-1">Fecha Registro:</label>
                <input
                  type="date"
                  name="fechaRegistro"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                  value={editingRecord.fechaRegistro}
                  onChange={handleEditingProductionChange}
                />
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-1">Tipo Producci√≥n:</label>
                <input
                  type="text"
                  name="tipoProduccion"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                  value={editingRecord.tipoProduccion}
                  onChange={handleEditingProductionChange}
                />
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-1">Cantidad:</label>
                <input
                  type="number"
                  name="cantidad"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                  value={editingRecord.cantidad}
                  onChange={handleEditingProductionChange}
                />
              </div>
              <div>
                <label className="block text-gray-700 text-sm font-bold mb-1">Unidad Medida:</label>
                <input
                  type="text"
                  name="unidadMedida"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                  value={editingRecord.unidadMedida}
                  onChange={handleEditingProductionChange}
                />
              </div>
              <div className="col-span-full">
                <label className="block text-gray-700 text-sm font-bold mb-1">Observaciones:</label>
                <textarea
                  name="observaciones"
                  rows="3"
                  className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                  value={editingRecord.observaciones}
                  onChange={handleEditingProductionChange}
                ></textarea>
              </div>
            </div>
            <div className="mt-6 flex justify-end space-x-3">
              <button
                onClick={saveEditedProductionRecord}
                className="bg-green-600 text-white py-2 px-5 rounded-lg hover:bg-green-700 transition-colors shadow-md"
              >
                Guardar Cambios
              </button>
              <button
                onClick={cancelEditingProductionRecord}
                className="bg-gray-600 text-white py-2 px-5 rounded-lg hover:bg-gray-700 transition-colors shadow-md"
              >
                Cancelar Edici√≥n
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductionControlPage;

