import React, { useState } from 'react';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

const AnimalRegistrationPage = ({ onRegisterAnimal, onUpdateAnimalProduction, animals, currentUser }) => {
  const [animalData, setAnimalData] = useState({
    id: '',
    name: '',
    raza: '',
    peso: '',
    sexo: '',
    fechaNacimiento: '',
    fechaCompra: '',
    tipoProduccion: '',
    dailyMilk: 0,
    foto: null,
    idpadre: '',
    idmadre: '',
    status: 'Desarrollo',
  });
  const [selectedAnimalForGenealogy, setSelectedAnimalForGenealogy] = useState('');
  const [genealogyTree, setGenealogyTree] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAnimalData({ ...animalData, [name]: value });
  };

  const handleFileChange = (e) => {
    setAnimalData({ ...animalData, foto: e.target.files[0] });
  };

  const handleSubmit = () => {
    const newAnimalId = `AN-${Date.now().toString().slice(-5)}`;
    const animalToRegister = { ...animalData, id: newAnimalId };
    onRegisterAnimal(animalToRegister);
    alert(`Animal ${animalToRegister.name} (${newAnimalId}) registrado.`);
    setAnimalData({
      id: '', name: '', raza: '', peso: '', sexo: '', fechaNacimiento: '', fechaCompra: '',
      tipoProduccion: '', dailyMilk: 0, foto: null, Idpadre: '', Idmadre: '',
      status: 'desarrollo',
    });
  };

  const handleDownloadPDF = (animal, currentUser) => {
  if (!animal || !animal.id) {
    alert('Por favor, selecciona un animal válido.');
    return;
  }

  const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
  const adminName = currentUser?.fullName || 'Administrador';
  const logoPlaceholder = 'https://via.placeholder.com/60x60?text=LOGO';
  const qrURL = `https://api.qrserver.com/v1/create-qr-code/?data=${animal.id}&size=80x80`;

  const tempDiv = document.createElement('div');
  tempDiv.style.width = '210mm';
  tempDiv.style.padding = '20mm';
  tempDiv.style.fontFamily = 'Arial, sans-serif';
  tempDiv.style.fontSize = '11pt';
  tempDiv.style.backgroundColor = '#fff';
  tempDiv.style.position = 'absolute';
  tempDiv.style.left = '-9999px';
  tempDiv.style.top = '-9999px';

  const colorEstado = animal.status === 'en_produccion' ? '#27ae60' :
                      animal.status === 'seca' ? '#e67e22' :
                      animal.status === 'preñada' ? '#9b59b6' : '#7f8c8d';

  tempDiv.innerHTML = `
    <div style="border: 2px solid #ccc; border-radius: 10px; padding: 20px;">
      <div style="display: flex; justify-content: space-between; align-items: center;">
        <img src="${logoPlaceholder}" style="width: 60px;" alt="Logo Finca">
        <div style="text-align: right;">
          <h1 style="margin: 0; color: #2c3e50;">${farmName}</h1>
          <p style="margin: 0; color: #7f8c8d;">Ficha de Registro Animal</p>
        </div>
      </div>

      <div style="display: flex; margin-top: 20px; gap: 20px;">
        <div style="flex-shrink: 0;">
          <img src="${URL.createObjectURL(animal.foto)}" style="width: 120px; border-radius: 8px; border: 1px solid #ccc;" alt="Foto del animal">
        </div>
        <div style="flex-grow: 1;">
          <table style="width: 100%; border-collapse: collapse; border: 2px solid #000;">
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 12px;"><strong>Nombre:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.name || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10x 16px;"><strong>ID:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.id || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Raza:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.raza || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Raza:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.peso || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Sexo:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.sexo || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Fecha Nacimiento:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.fechaNacimiento || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Fecha Compra:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.fechaCompra || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>ID Padre:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.Idpadre || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>ID Madre:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.Idmadre || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Estado:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px; color: ${colorEstado};">${animal.status || 'N/A'}</td></tr>
            <tr style="border-bottom: 2px solid #000;"><td style="border: 2px solid #000;" style="padding: 10px 16px;"><strong>Tipo Producción:</strong></td><td style="border: 2px solid #000;" style="padding: 10px 16px;">${animal.tipoProduccion || 'N/A'}</td></tr>
          </table>
        </div>
      </div>

      <div style="text-align: center; margin-top: 30px;">
        <img src="${qrURL}" style="width: 80px;" alt="QR Code del animal">
      </div>

      <p style="text-align: right; font-size: 10px; margin-top: 30px; color: #aaa;">
        Generado por Campo Inteligente | ${adminName}
      </p>
    </div>
  `;

  document.body.appendChild(tempDiv);

  html2canvas(tempDiv, { scale: 2 }).then(canvas => {
    const imgData = canvas.toDataURL('image/png');
    const pdf = new jsPDF('p', 'mm', 'a4');
    const imgWidth = 210;
    const pageHeight = 297;
    const imgHeight = canvas.height * imgWidth / canvas.width;
    let heightLeft = imgHeight;
    let position = 0;

    pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
    heightLeft -= pageHeight;

    while (heightLeft > 0) {
      position = heightLeft - imgHeight;
      pdf.addPage();
      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;
    }

    pdf.save(`ficha_animal_${animal.name || 'sin_nombre'}_${animal.id || 'sin_id'}.pdf`);
    document.body.removeChild(tempDiv);
  });
};

  const handleUpdateProduction = () => {
    if (animalData.id) {
      onUpdateAnimalProduction(animalData.id, animalData.status, animalData.dailyMilk);
      alert(`Producción/Estado de ${animalData.id} actualizado.`);
    } else {
      alert('Por favor, ingresa el ID del animal para actualizar su producción/estado.');
    }
  };

  // --- Lógica del Árbol Genealógico ---
  const buildGenealogyTree = (animalId, allAnimals) => {
    const animal = allAnimals.find(a => a.id === animalId);
    if (!animal) return null;

    const tree = {
      id: animal.id,
      name: animal.name,
      raza: animal.raza,
      sexo: animal.sexo,
      children: []
    };

    // Buscar hijos (simplificado: solo busca una generación hacia abajo)
    const children = allAnimals.filter(a => a.padreId === animal.id || a.madreId === animal.id);
    if (children.length > 0) {
      tree.children = children.map(child => ({
        id: child.id,
        name: child.name,
        raza: child.raza,
        sexo: child.sexo,
        // Para un árbol completo, esto sería recursivo:
        // children: buildGenealogyTree(child.id, allAnimals).children
      }));
    }

    // Buscar padres (una generación hacia arriba)
    if (animal.Idpadre) {
      tree.father = allAnimals.find(a => a.id === animal.Idpadre);
    }
    if (animal.Idmadre) {
      tree.mother = allAnimals.find(a => a.id === animal.Idmadre);
    }

    return tree;
  };

  const handleViewGenealogy = () => {
    if (!selectedAnimalForGenealogy) {
      alert('Por favor, selecciona un animal para ver su árbol genealógico.');
      return;
    }
    const tree = buildGenealogyTree(selectedAnimalForGenealogy, animals);
    setGenealogyTree(tree);
    if (!tree) {
      alert('Animal no encontrado o sin información genealógica.');
    }
  };

  const handleDownloadGenealogyJPG = () => {
    if (!genealogyTree) {
      alert('Primero visualiza un árbol genealógico para descargarlo.');
      return;
    }

    const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
    const adminName = currentUser?.fullName || 'Administrador';
    const logoPlaceholder = 'https://via.placeholder.com/50x50?text=LOGO';

    // Crear un div temporal para el árbol genealógico
    const treeDiv = document.createElement('div');
    treeDiv.style.width = '297mm'; // Ancho A4 horizontal
    treeDiv.style.height = '210mm'; // Alto A4 horizontal
    treeDiv.style.padding = '10mm';
    treeDiv.style.fontFamily = 'Arial, sans-serif';
    treeDiv.style.fontSize = '8pt';
    treeDiv.style.color = '#333';
    treeDiv.style.position = 'absolute';
    treeDiv.style.left = '-9999px';
    treeDiv.style.top = '-9999px';
    treeDiv.style.backgroundColor = '#fff';
    treeDiv.style.display = 'flex';
    treeDiv.style.flexDirection = 'column';
    treeDiv.style.justifyContent = 'space-between';

    // Función para renderizar un nodo del árbol (simplificado)
    const renderNode = (node, level = 0) => {
      let html = `<div style="margin-left: ${level * 10}mm; margin-bottom: 5mm; border: 1px solid #ccc; padding: 5mm; border-radius: 5px; background-color: #f9f9f9;">`;
      html += `<p><strong>${node.name}</strong> (${node.id})</p>`;
      html += `<p>Raza: ${node.raza}, Sexo: ${node.sexo}</p>`;
      if (node.father || node.mother || node.children.length > 0) {
        html += `<div style="margin-top: 5mm;">`;
        if (node.father) html += `<p>Padre: ${node.father.name} (${node.father.id})</p>`;
        if (node.mother) html += `<p>Madre: ${node.mother.name} (${node.mother.id})</p>`;
        if (node.children.length > 0) {
          html += `<p>Hijos:</p><ul style="list-style: none; padding: 0; margin: 0;">`;
          node.children.forEach(child => {
            html += `<li style="margin-left: 10mm;">${child.name} (${child.id})</li>`;
          });
          html += `</ul>`;
        }
        html += `</div>`;
      }
      html += `</div>`;
      return html;
    };

    treeDiv.innerHTML = `
      <div style="display: flex; justify-content: space-between; align-items: center; margin-bottom: 10mm; padding-bottom: 5mm; border-bottom: 1px solid #eee;">
        <img src="${logoPlaceholder}" alt="Logo Finca" style="width: 15mm; height: 15mm; object-fit: contain;" />
        <div style="text-align: right;">
          <h1 style="font-size: 16pt; margin: 0; color: #2c3e50;">${farmName}</h1>
          <p style="font-size: 9pt; margin: 0; color: #7f8c8d;">Árbol Genealógico</p>
        </div>
      </div>
      <div style="flex-grow: 1; overflow: auto; display: flex; flex-direction: column; align-items: flex-start;">
        ${renderNode(genealogyTree)}
        ${genealogyTree.father ? renderNode(genealogyTree.father, 1) : ''}
        ${genealogyTree.mother ? renderNode(genealogyTree.mother, 1) : ''}
        ${genealogyTree.children.map(child => renderNode(child, 1)).join('')}
      </div>
      <div style="text-align: center; margin-top: 10mm; font-size: 8pt; color: #7f8c8d; border-top: 1px solid #eee; padding-top: 5mm;">
        <p>Generado por Campo Inteligente</p>
        <p>Administrador: ${adminName}</p>
      </div>
    `;

    document.body.appendChild(treeDiv);

    html2canvas(treeDiv, { scale: 2 }).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('l', 'mm', 'a4'); // 'l' para orientación horizontal
      const imgWidth = 297; // A4 horizontal width in mm
      const pageHeight = 210; // A4 horizontal height in mm
      const imgHeight = canvas.height * imgWidth / canvas.width;
      let heightLeft = imgHeight;
      let position = 0;

      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }
      pdf.save(`arbol_genealogico_${genealogyTree.name || 'sin_nombre'}_${genealogyTree.id || 'sin_id'}.pdf`);
      document.body.removeChild(treeDiv);
    });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Registro Animal Avanzado</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          type="text"
          name="name"
          placeholder="Nombre del Animal"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.name}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="raza"
          placeholder="Raza"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.raza}
          onChange={handleInputChange}

        />
          type="number"
          name="peso"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.sexo}
          onChange={handleInputChange}

        />
        <select
          name="sexo"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.sexo}
          onChange={handleInputChange}
        >
          <option value="">Selecciona Sexo</option>
          <option value="macho">Macho</option>
          <option value="hembra">Hembra</option>
        </select>
        <input
          type="date"
          name="fechaNacimiento"
          placeholder="Fecha de Nacimiento"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.fechaNacimiento}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="fechaCompra"
          placeholder="Fecha de Compra (Opcional)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.fechaCompra}
          onChange={handleInputChange}
        />
        <select
          name="tipoProduccion"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.tipoProduccion}
          onChange={handleInputChange}
        >
          <option value="">Tipo de Producción</option>
          <option value="carne">Carne</option>
          <option value="leche">Leche</option>
        </select>
        {animalData.tipoProduccion === 'leche' && animalData.sexo === 'hembra' && (
          <input
            type="number"
            name="dailyMilk"
            placeholder="Litros Promedio Diarios"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={animalData.dailyMilk}
            onChange={handleInputChange}
          />
        )}
        <input
          type="text"
          name="Idpadre"
          placeholder="ID Padre (Opcional)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.padreId}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="Idmadre"
          placeholder="ID Madre (Opcional)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={animalData.madreId}
          onChange={handleInputChange}
        />
        <div className="col-span-full">
          <label className="block text-gray-700 text-sm font-bold mb-2">Foto del Animal</label>
          <input
            type="file"
            name="foto"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            onChange={handleFileChange}
          />
        </div>
      </div>
      <button
        onClick={handleSubmit}
        className="mt-6 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors mr-2"
      >
        Registrar Animal
      </button>
      <button
        onClick={() => handleDownloadPDF(animalData)} // Pasar el animalData actual para generar PDF
        className="mt-6 bg-gray-600 text-white py-2 px-4 rounded-lg hover:bg-gray-700 transition-colors"
      >
        Descargar Ficha PDF
      </button>

      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Actualizar Producción/Estado</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <input
            type="text"
            name="id"
            placeholder="ID del Animal a Actualizar"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={animalData.id}
            onChange={handleInputChange}
          />
          <select
            name="status"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={animalData.status}
            onChange={handleInputChange}
          >
            <option value="desarrollo">En Desarrollo</option>
            <option value="en_produccion">En Producción</option>
            <option value="seca">Seca</option>
            <option value="preñada">Preñada</option>
          </select>
          {animalData.status === 'en_produccion' && animalData.tipoProduccion === 'leche' && (
            <input
              type="number"
              name="dailyMilk"
              placeholder="Litros de leche diarios"
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
              value={animalData.dailyMilk}
              onChange={handleInputChange}
            />
          )}
        </div>
        <button
          onClick={handleUpdateProduction}
          className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors"
        >
          Actualizar Producción
        </button>
      </div>

      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Árbol Genealógico</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={selectedAnimalForGenealogy}
            onChange={(e) => setSelectedAnimalForGenealogy(e.target.value)}
          >
            <option value="">Selecciona Animal para Genealogía</option>
            {animals.map(animal => (
              <option key={animal.id} value={animal.id}>{animal.name} ({animal.id})</option>
            ))}
          </select>
          <button
            onClick={handleViewGenealogy}
            className="bg-purple-600 text-white py-2 px-4 rounded-lg hover:bg-purple-700 transition-colors"
          >
            Ver Árbol Genealógico
          </button>
        </div>

        {genealogyTree && (
          <div className="mt-4 p-4 border rounded-lg bg-gray-50">
            <h5 className="font-semibold">Árbol Genealógico de {genealogyTree.name} ({genealogyTree.id})</h5>
            <p>Raza: {genealogyTree.raza}, Sexo: {genealogyTree.sexo}</p>
            {genealogyTree.father && (
              <p className="ml-4">Padre: {genealogyTree.father.name} ({genealogyTree.father.id})</p>
            )}
            {genealogyTree.mother && (
              <p className="ml-4">Madre: {genealogyTree.mother.name} ({genealogyTree.mother.id})</p>
            )}
            {genealogyTree.children.length > 0 && (
              <div className="mt-2">
                <p className="font-semibold">Hijos:</p>
                <ul className="list-disc list-inside ml-4">
                  {genealogyTree.children.map(child => (
                    <li key={child.id}>{child.name} ({child.id}) - {child.raza}</li>
                  ))}
                </ul>
              </div>
            )}
            <button
              onClick={handleDownloadGenealogyJPG}
              className="mt-4 bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-colors"
            >
              Descargar Árbol (JPG)
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AnimalRegistrationPage;
