import React, { useState } from 'react';

const ReproductiveControlPage = () => {
  const [reproData, setReproData] = useState({
    animalId: '',
    tipoMonta: '',
    toroSemen: '',
    fechaCelo: '',
    fechaMonta: '',
    inseminador: '',
    resultado: '',
    fechaEcografia: '',
    fechaParto: '',
    diasSeco: '',
    retornoProduccion: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setReproData({ ...reproData, [name]: value });
  };

  const handleSubmit = () => {
    console.log('Registrando control reproductivo:', reproData);
    alert('Control reproductivo registrado (simulado)');
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Control Reproductivo y de Gestación</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          type="text"
          name="animalId"
          placeholder="ID Animal (Vaca)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.animalId}
          onChange={handleInputChange}
        />
        <select
          name="tipoMonta"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.tipoMonta}
          onChange={handleInputChange}
        >
          <option value="">Tipo de Monta</option>
          <option value="natural">Natural</option>
          <option value="inseminacion">Inseminación</option>
        </select>
        <input
          type="text"
          name="toroSemen"
          placeholder="Toro / Semen Utilizado"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.toroSemen}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="fechaCelo"
          placeholder="Fecha del Celo"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.fechaCelo}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="fechaMonta"
          placeholder="Fecha de Monta"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.fechaMonta}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="inseminador"
          placeholder="Inseminador (Opcional)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.inseminador}
          onChange={handleInputChange}
        />
        <select
          name="resultado"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.resultado}
          onChange={handleInputChange}
        >
          <option value="">Resultado</option>
          <option value="positiva">Preñada</option>
          <option value="negativa">No Preñada</option>
        </select>
        <input
          type="date"
          name="fechaEcografia"
          placeholder="Fecha Ecografía"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.fechaEcografia}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="fechaParto"
          placeholder="Fecha de Parto"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.fechaParto}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="diasSeco"
          placeholder="Días en Seco"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.diasSeco}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="retornoProduccion"
          placeholder="Retorno a Producción"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={reproData.retornoProduccion}
          onChange={handleInputChange}
        />
      </div>
      <button
        onClick={handleSubmit}
        className="mt-6 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
      >
        Registrar Evento Reproductivo
      </button>
      <div className="mt-6 p-4 bg-blue-100 border border-blue-300 rounded-lg text-blue-800">
        <p>Calendario de gestación y vacas preñadas aparecerán aquí (simulado).</p>
      </div>
    </div>
  );
};

export default ReproductiveControlPage;
