import React, { useState } from 'react';

const SanitaryControlPage = () => {
  const [treatmentData, setTreatmentData] = useState({
    animalId: '',
    product: '',
    lote: '',
    unidad: '',
    dosis: '',
    fechaAplicacion: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTreatmentData({ ...treatmentData, [name]: value });
  };

  const handleSubmit = () => {
    console.log('Registrando tratamiento:', treatmentData);
    alert('Tratamiento registrado (simulado)');
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Control Sanitario con Stock Inteligente</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <input
          type="text"
          name="animalId"
          placeholder="ID Animal"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={treatmentData.animalId}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="product"
          placeholder="Producto (Vacuna, Vitamina, etc.)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={treatmentData.product}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="lote"
          placeholder="Lote del Producto"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={treatmentData.lote}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="unidad"
          placeholder="Unidad (Ej: ml, cc, unidades)"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={treatmentData.unidad}
          onChange={handleInputChange}
        />
        <input
          type="number"
          name="dosis"
          placeholder="Dosis Aplicada"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={treatmentData.dosis}
          onChange={handleInputChange}
        />
        <input
          type="date"
          name="fechaAplicacion"
          placeholder="Fecha de Aplicación"
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
          value={treatmentData.fechaAplicacion}
          onChange={handleInputChange}
        />
      </div>
      <button
        onClick={handleSubmit}
        className="mt-6 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
      >
        Registrar Tratamiento
      </button>
      <div className="mt-6 p-4 bg-yellow-100 border border-yellow-300 rounded-lg text-yellow-800">
        <p>Notificaciones de stock y vencimiento aparecerán aquí (simulado).</p>
      </div>
    </div>
  );
};

export default SanitaryControlPage;
