import React, { useState } from 'react';

const SupplierClientManagement = ({ suppliers, clients, debts, credits, addSupplier, addClient, addDebt, payDebt, addCredit, collectCredit }) => {
  const [newSupplier, setNewSupplier] = useState({ name: '', contact: '', phone: '', email: '' });
  const [newClient, setNewClient] = useState({ name: '', contact: '', phone: '', email: '' });
  const [newDebt, setNewDebt] = useState({ supplierId: '', amount: '', description: '', dueDate: '', paid: false });
  const [newCredit, setNewCredit] = useState({ clientId: '', amount: '', description: '', dueDate: '', collected: false });

  const handleAddSupplier = () => {
    if (newSupplier.name && newSupplier.contact) {
      addSupplier(newSupplier);
      setNewSupplier({ name: '', contact: '', phone: '', email: '' });
    } else {
      alert('Nombre y Contacto del proveedor son obligatorios.');
    }
  };

  const handleAddClient = () => {
    if (newClient.name && newClient.contact) {
      addClient(newClient);
      setNewClient({ name: '', contact: '', phone: '', email: '' });
    } else {
      alert('Nombre y Contacto del cliente son obligatorios.');
    }
  };

  const handleAddDebt = () => {
    if (newDebt.supplierId && newDebt.amount && newDebt.dueDate) {
      addDebt(newDebt);
      setNewDebt({ supplierId: '', amount: '', description: '', dueDate: '', paid: false });
    } else {
      alert('Proveedor, Monto y Fecha de Vencimiento de la deuda son obligatorios.');
    }
  };

  const handleAddCredit = () => {
    if (newCredit.clientId && newCredit.amount && newCredit.dueDate) {
      addCredit(newCredit);
      setNewCredit({ clientId: '', amount: '', description: '', dueDate: '', collected: false });
    } else {
      alert('Cliente, Monto y Fecha de Vencimiento del crédito son obligatorios.');
    }
  };

  return (
    <div className="mt-8 p-6 bg-white rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Gestión de Proveedores y Clientes</h3>

      {/* Sección de Proveedores */}
      <div className="mb-8 border-b pb-4">
        <h4 className="text-lg font-medium mb-3">Proveedores</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <input
            type="text"
            placeholder="Nombre del Proveedor"
            value={newSupplier.name}
            onChange={(e) => setNewSupplier({ ...newSupplier, name: e.target.value })}
            className="p-2 border rounded-md"
          />
          <input
            type="text"
            placeholder="Contacto"
            value={newSupplier.contact}
            onChange={(e) => setNewSupplier({ ...newSupplier, contact: e.target.value })}
            className="p-2 border rounded-md"
          />
          <input
            type="text"
            placeholder="Teléfono"
            value={newSupplier.phone}
            onChange={(e) => setNewSupplier({ ...newSupplier, phone: e.target.value })}
            className="p-2 border rounded-md"
          />
          <input
            type="email"
            placeholder="Email"
            value={newSupplier.email}
            onChange={(e) => setNewSupplier({ ...newSupplier, email: e.target.value })}
            className="p-2 border rounded-md"
          />
        </div>
        <button onClick={handleAddSupplier} className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
          Agregar Proveedor
        </button>

        <div className="mt-4">
          <h5 className="font-medium mb-2">Lista de Proveedores</h5>
          <ul className="list-disc pl-5">
            {suppliers.map(supplier => (
              <li key={supplier.id} className="text-sm text-gray-700">
                {supplier.name} ({supplier.contact}) - {supplier.phone}
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Sección de Clientes */}
      <div className="mb-8 border-b pb-4">
        <h4 className="text-lg font-medium mb-3">Clientes</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
          <input
            type="text"
            placeholder="Nombre del Cliente"
            value={newClient.name}
            onChange={(e) => setNewClient({ ...newClient, name: e.target.value })}
            className="p-2 border rounded-md"
          />
          <input
            type="text"
            placeholder="Contacto"
            value={newClient.contact}
            onChange={(e) => setNewClient({ ...newClient, contact: e.target.value })}
            className="p-2 border rounded-md"
          />
          <input
            type="text"
            placeholder="Teléfono"
            value={newClient.phone}
            onChange={(e) => setNewClient({ ...newClient, phone: e.target.value })}
            className="p-2 border rounded-md"
          />
          <input
            type="email"
            placeholder="Email"
            value={newClient.email}
            onChange={(e) => setNewClient({ ...newClient, email: e.target.value })}
            className="p-2 border rounded-md"
          />
        </div>
        <button onClick={handleAddClient} className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
          Agregar Cliente
        </button>

        <div className="mt-4">
          <h5 className="font-medium mb-2">Lista de Clientes</h5>
          <ul className="list-disc pl-5">
            {clients.map(client => (
              <li key={client.id} className="text-sm text-gray-700">
                {client.name} ({client.contact}) - {client.phone}
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Sección de Deudas (Facturas por Pagar) */}
      <div className="mb-8 border-b pb-4">
        <h4 className="text-lg font-medium mb-3">Deudas (Facturas por Pagar)</h4>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
          <select
            value={newDebt.supplierId}
            onChange={(e) => setNewDebt({ ...newDebt, supplierId: e.target.value })}
            className="p-2 border rounded-md"
          >
            <option value="">Selecciona Proveedor</option>
            {suppliers.map(supplier => (
              <option key={supplier.id} value={supplier.id}>{supplier.name}</option>
            ))}
          </select>
          <input
            type="number"
            placeholder="Monto"
            value={newDebt.amount}
            onChange={(e) => setNewDebt({ ...newDebt, amount: parseFloat(e.target.value) })}
            className="p-2 border rounded-md"
          />
          <input
            type="date"
            placeholder="Fecha de Vencimiento"
            value={newDebt.dueDate}
            onChange={(e) => setNewDebt({ ...newDebt, dueDate: e.target.value })}
            className="p-2 border rounded-md"
          />
          <textarea
            placeholder="Descripción de la deuda"
            value={newDebt.description}
            onChange={(e) => setNewDebt({ ...newDebt, description: e.target.value })}
            className="p-2 border rounded-md col-span-full"
          ></textarea>
        </div>
        <button onClick={handleAddDebt} className="bg-red-500 text-white px-4 py-2 rounded-md hover:bg-red-600 transition">
          Registrar Deuda
        </button>

        <div className="mt-4">
          <h5 className="font-medium mb-2">Deudas Pendientes</h5>
          <ul className="list-disc pl-5">
            {debts.filter(d => !d.paid).map(debt => (
              <li key={debt.id} className="text-sm text-gray-700 flex justify-between items-center">
                <span>
                  {suppliers.find(s => s.id === debt.supplierId)?.name || 'N/A'} - ${debt.amount.toFixed(2)} (Vence: {debt.dueDate}) - {debt.description}
                </span>
                <button onClick={() => payDebt(debt.id)} className="ml-4 bg-green-500 text-white px-3 py-1 rounded-md text-xs hover:bg-green-600 transition">
                  Marcar como Pagada
                </button>
              </li>
            ))}
          </ul>
          <h5 className="font-medium mt-4 mb-2">Deudas Pagadas</h5>
          <ul className="list-disc pl-5 text-gray-500">
            {debts.filter(d => d.paid).map(debt => (
              <li key={debt.id} className="text-sm line-through">
                {suppliers.find(s => s.id === debt.supplierId)?.name || 'N/A'} - ${debt.amount.toFixed(2)} (Pagada: {debt.paymentDate})
              </li>
            ))}
          </ul>
        </div>
      </div>

      {/* Sección de Créditos (Cuentas por Cobrar) */}
      <div>
        <h4 className="text-lg font-medium mb-3">Créditos (Cuentas por Cobrar)</h4>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
          <select
            value={newCredit.clientId}
            onChange={(e) => setNewCredit({ ...newCredit, clientId: e.target.value })}
            className="p-2 border rounded-md"
          >
            <option value="">Selecciona Cliente</option>
            {clients.map(client => (
              <option key={client.id} value={client.id}>{client.name}</option>
            ))}
          </select>
          <input
            type="number"
            placeholder="Monto"
            value={newCredit.amount}
            onChange={(e) => setNewCredit({ ...newCredit, amount: parseFloat(e.target.value) })}
            className="p-2 border rounded-md"
          />
          <input
            type="date"
            placeholder="Fecha de Vencimiento"
            value={newCredit.dueDate}
            onChange={(e) => setNewCredit({ ...newCredit, dueDate: e.target.value })}
            className="p-2 border rounded-md"
          />
          <textarea
            placeholder="Descripción del crédito"
            value={newCredit.description}
            onChange={(e) => setNewCredit({ ...newCredit, description: e.target.value })}
            className="p-2 border rounded-md col-span-full"
          ></textarea>
        </div>
        <button onClick={handleAddCredit} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600 transition">
          Registrar Crédito
        </button>

        <div className="mt-4">
          <h5 className="font-medium mb-2">Créditos Pendientes</h5>
          <ul className="list-disc pl-5">
            {credits.filter(c => !c.collected).map(credit => (
              <li key={credit.id} className="text-sm text-gray-700 flex justify-between items-center">
                <span>
                  {clients.find(cl => cl.id === credit.clientId)?.name || 'N/A'} - ${credit.amount.toFixed(2)} (Vence: {credit.dueDate}) - {credit.description}
                </span>
                <button onClick={() => collectCredit(credit.id)} className="ml-4 bg-blue-500 text-white px-3 py-1 rounded-md text-xs hover:bg-blue-600 transition">
                  Marcar como Cobrado
                </button>
              </li>
            ))}
          </ul>
          <h5 className="font-medium mt-4 mb-2">Créditos Cobrados</h5>
          <ul className="list-disc pl-5 text-gray-500">
            {credits.filter(c => c.collected).map(credit => (
              <li key={credit.id} className="text-sm line-through">
                {clients.find(cl => cl.id === credit.clientId)?.name || 'N/A'} - ${credit.amount.toFixed(2)} (Cobrado: {credit.collectionDate})
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default SupplierClientManagement;
