import React, { useState } from 'react';

const AuthLoginForm = ({ onLoginSuccess, onNavigateToRegister, registeredUsers }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false); // Nuevo estado para la animación de carga

  const handleLogin = () => {
    setError(''); // Limpiar errores previos
    setIsLoading(true); // Iniciar animación de carga

    // Simular una llamada a API con un retraso
    setTimeout(() => {
      const user = registeredUsers.find(u => u.email === email);

      if (!user) {
        setError('Correo no registrado. Por favor, regístrate.');
        setIsLoading(false); // Detener animación de carga
        return;
      }

      // Simulación de verificación de contraseña (en un entorno real, se compararía un hash)
      if (user.password !== password) { // Asumiendo que la contraseña se guarda en texto plano para esta simulación
        setError('Contraseña incorrecta. Intenta de nuevo.');
        setIsLoading(false); // Detener animación de carga
        return;
      }

      onLoginSuccess(user);
      setIsLoading(false); // Detener animación de carga (en caso de éxito)
    }, 1500); // Simula 1.5 segundos de carga
  };

  return (
    <div className="p-6 bg-white rounded-xl shadow-2xl border border-gray-100">
      <h2 className="text-3xl font-extrabold text-center text-gray-900 mb-8">
        Bienvenido de Nuevo
      </h2>
      {error && (
        <p className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded-lg relative mb-6 text-sm text-center">
          {error}
        </p>
      )}
      <div className="space-y-5">
        <input
          type="email"
          placeholder="Correo Electrónico"
          className="w-full px-5 py-3 border border-gray-300 rounded-lg shadow-sm placeholder-gray-500
                     focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent
                     transition duration-200 ease-in-out text-base"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          disabled={isLoading} // Deshabilitar input durante la carga
        />
        <input
          type="password"
          placeholder="Contraseña"
          className="w-full px-5 py-3 border border-gray-300 rounded-lg shadow-sm placeholder-gray-500
                     focus:outline-none focus:ring-2 focus:ring-black focus:border-transparent
                     transition duration-200 ease-in-out text-base"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          disabled={isLoading} // Deshabilitar input durante la carga
        />
        <button
          onClick={handleLogin}
          className="w-full bg-black text-white py-3 rounded-lg font-semibold text-lg
                     hover:bg-gray-800 transition duration-200 ease-in-out shadow-md
                     focus:outline-none focus:ring-2 focus:ring-black focus:ring-offset-2
                     flex items-center justify-center" // Para centrar el spinner
          disabled={isLoading} // Deshabilitar botón durante la carga
        >
          {isLoading ? (
            <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
            </svg>
          ) : (
            'Iniciar Sesión'
          )}
        </button>
      </div>
      <p className="text-center mt-6 text-gray-600 text-sm">
        ¿No tienes una cuenta?{' '}
        <button
          onClick={onNavigateToRegister}
          className="text-black font-bold hover:underline focus:outline-none"
          disabled={isLoading} // Deshabilitar botón durante la carga
        >
          Regístrate aquí
        </button>
      </p>
    </div>
  );
};

export default AuthLoginForm;
