import React, { useState, useEffect } from 'react';

const SanitaryControlPage = ({ inventoryItems, onUpdateInventoryQuantity, animals }) => {
  const [treatmentData, setTreatmentData] = useState({
    animalId: '',
    productId: '', // Ahora se usa el ID del producto del inventario
    dosis: '',
    fechaAplicacion: '',
    observaciones: '',
  });
  const [diseaseData, setDiseaseData] = useState({
    animalId: '',
    diseaseName: '',
    symptoms: '',
    diagnosisDate: '',
    treatmentProductId: '', // Medicamento para la enfermedad
    treatmentDosis: '',
    treatmentStartDate: '',
    treatmentEndDate: '',
    recoveryDate: '',
    diseaseObservations: '',
  });

  const [treatmentsHistory, setTreatmentsHistory] = useState([]); // Historial de tratamientos
  const [diseasesHistory, setDiseasesHistory] = useState([]); // Historial de enfermedades
  const [notifications, setNotifications] = useState([]); // Notificaciones de stock y vencimiento
  const [vaccinationSchedule, setVaccinationSchedule] = useState([]); // Calendario de vacunación
  const [dewormingSchedule, setDewormingSchedule] = useState([]); // Calendario de desparasitación
  const [vitaminSchedule, setVitaminSchedule] = useState([]); // Calendario de vitaminas

  // Configuraciones de frecuencia
  const [dewormingFrequency, setDewormingFrequency] = useState(() => JSON.parse(localStorage.getItem('dewormingFrequency')) || { value: 3, unit: 'meses' });
  const [vitaminFrequency, setVitaminFrequency] = useState(() => JSON.parse(localStorage.getItem('vitaminFrequency')) || { value: 6, unit: 'meses' });
  const [stockThreshold, setStockThreshold] = useState(() => JSON.parse(localStorage.getItem('stockThreshold')) || 10); // Umbral de alerta de stock

  // Cargar historial de tratamientos, calendario de vacunación y desparasitación desde localStorage
  useEffect(() => {
    const storedTreatments = JSON.parse(localStorage.getItem('treatmentsHistory')) || [];
    setTreatmentsHistory(storedTreatments);
    const storedDiseases = JSON.parse(localStorage.getItem('diseasesHistory')) || [];
    setDiseasesHistory(storedDiseases);
    const storedVaccinationSchedule = JSON.parse(localStorage.getItem('vaccinationSchedule')) || [];
    setVaccinationSchedule(storedVaccinationSchedule);
    const storedDewormingSchedule = JSON.parse(localStorage.getItem('dewormingSchedule')) || [];
    setDewormingSchedule(storedDewormingSchedule);
    const storedVitaminSchedule = JSON.parse(localStorage.getItem('vitaminSchedule')) || [];
    setVitaminSchedule(storedVitaminSchedule);
  }, []);

  // Guardar historial de tratamientos, calendario de vacunación y desparasitación en localStorage
  useEffect(() => {
    localStorage.setItem('treatmentsHistory', JSON.stringify(treatmentsHistory));
  }, [treatmentsHistory]);

  useEffect(() => {
    localStorage.setItem('diseasesHistory', JSON.stringify(diseasesHistory));
  }, [diseasesHistory]);

  useEffect(() => {
    localStorage.setItem('vaccinationSchedule', JSON.stringify(vaccinationSchedule));
  }, [vaccinationSchedule]);

  useEffect(() => {
    localStorage.setItem('dewormingSchedule', JSON.stringify(dewormingSchedule));
  }, [dewormingSchedule]);

  useEffect(() => {
    localStorage.setItem('vitaminSchedule', JSON.stringify(vitaminSchedule));
  }, [vitaminSchedule]);

  useEffect(() => {
    localStorage.setItem('dewormingFrequency', JSON.stringify(dewormingFrequency));
  }, [dewormingFrequency]);

  useEffect(() => {
    localStorage.setItem('vitaminFrequency', JSON.stringify(vitaminFrequency));
  }, [vitaminFrequency]);

  useEffect(() => {
    localStorage.setItem('stockThreshold', JSON.stringify(stockThreshold));
  }, [stockThreshold]);

  // Generar notificaciones al cargar o actualizar inventario
  useEffect(() => {
    const newNotifications = [];
    inventoryItems.forEach(item => {
      // Notificación de bajo stock (ej. menos de 10 unidades)
      if (item.quantity < stockThreshold && item.category === 'medicamentos') {
        newNotifications.push(`¡Alerta! El stock de ${item.name} es bajo (${item.quantity} ${item.unit}).`);
      }
      // Notificación de vencimiento (ej. en los próximos 30 días)
      if (item.expiration) {
        const expirationDate = new Date(item.expiration);
        const today = new Date();
        const diffTime = expirationDate.getTime() - today.getTime();
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
        if (diffDays <= 30 && diffDays > 0) {
          newNotifications.push(`¡Atención! ${item.name} (Lote: ${item.lot}) vence en ${diffDays} días.`);
        } else if (diffDays <= 0) {
          newNotifications.push(`¡Urgente! ${item.name} (Lote: ${item.lot}) ha vencido.`);
        }
      }
    });

    // Notificaciones de vacunas próximas
    vaccinationSchedule.forEach(schedule => {
      const vaccineDate = new Date(schedule.date);
      const today = new Date();
      const diffTime = vaccineDate.getTime() - today.getTime();
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      if (diffDays <= 7 && diffDays >= 0) { // Próximas en 7 días
        newNotifications.push(`¡Recordatorio! Vacuna de ${schedule.vaccineName} para ${schedule.animalName} el ${schedule.date}.`);
      }
    });

    // Notificaciones de desparasitación próximas
    dewormingSchedule.forEach(schedule => {
      const dewormDate = new Date(schedule.date);
      const today = new Date();
      const diffTime = dewormDate.getTime() - today.getTime();
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      if (diffDays <= 7 && diffDays >= 0) { // Próximas en 7 días
        newNotifications.push(`¡Recordatorio! Desparasitación de ${schedule.dewormerName} para ${schedule.animalName} el ${schedule.date}.`);
      }
    });

    // Notificaciones de vitaminas próximas
    vitaminSchedule.forEach(schedule => {
      const vitaminDate = new Date(schedule.date);
      const today = new Date();
      const diffTime = vitaminDate.getTime() - today.getTime();
      const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
      if (diffDays <= 7 && diffDays >= 0) { // Próximas en 7 días
        newNotifications.push(`¡Recordatorio! Vitamina de ${schedule.vitaminName} para ${schedule.animalName} el ${schedule.date}.`);
      }
    });

    setNotifications(newNotifications);
  }, [inventoryItems, vaccinationSchedule, dewormingSchedule, vitaminSchedule, stockThreshold]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTreatmentData({ ...treatmentData, [name]: value });
  };

  const handleDiseaseInputChange = (e) => {
    const { name, value } = e.target;
    setDiseaseData({ ...diseaseData, [name]: value });
  };

  const handleSubmitTreatment = () => {
    const { animalId, productId, dosis, fechaAplicacion, observaciones } = treatmentData;

    if (!animalId || !productId || !dosis || !fechaAplicacion) {
      alert('Por favor, completa todos los campos obligatorios.');
      return;
    }

    const productInStock = inventoryItems.find(item => item.id === productId);
    if (!productInStock) {
      alert('Producto no encontrado en el inventario.');
      return;
    }
    if (productInStock.quantity < dosis) {
      alert(`No hay suficiente ${productInStock.name} en stock. Cantidad disponible: ${productInStock.quantity} ${productInStock.unit}.`);
      return;
    }

    // Registrar el tratamiento
    const newTreatment = {
      id: `TRT-${Date.now().toString().slice(-5)}`,
      animalId,
      productName: productInStock.name,
      productId,
      dosis: parseFloat(dosis),
      unit: productInStock.unit,
      fechaAplicacion,
      observaciones,
    };
    const updatedTreatmentsHistory = [...treatmentsHistory, newTreatment];
    setTreatmentsHistory(updatedTreatmentsHistory);

    // Actualizar el stock del producto
    onUpdateInventoryQuantity(productId, parseFloat(dosis));

    const animalName = animals.find(a => a.id === animalId)?.name || 'Desconocido';

    // Si es una vacuna, agregar al calendario de vacunación
    if (productInStock.category === 'medicamentos' && productInStock.name.toLowerCase().includes('vacuna')) {
      const newVaccineSchedule = {
        id: `VAC-${Date.now().toString().slice(-5)}`,
        animalId,
        animalName,
        vaccineName: productInStock.name,
        date: fechaAplicacion,
        observaciones,
      };
      setVaccinationSchedule(prev => [...prev, newVaccineSchedule]);
    }

    // Si es un desparasitante, agregar al calendario de desparasitación
    if (productInStock.category === 'medicamentos' && productInStock.name.toLowerCase().includes('desparasitante')) {
      const newDewormingSchedule = {
        id: `DEW-${Date.now().toString().slice(-5)}`,
        animalId,
        animalName,
        dewormerName: productInStock.name,
        date: fechaAplicacion,
        observaciones,
      };
      setDewormingSchedule(prev => [...prev, newDewormingSchedule]);
    }

    // Si es una vitamina, agregar al calendario de vitaminas
    if (productInStock.category === 'medicamentos' && productInStock.name.toLowerCase().includes('vitamina')) {
      const newVitaminSchedule = {
        id: `VIT-${Date.now().toString().slice(-5)}`,
        animalId,
        animalName,
        vitaminName: productInStock.name,
        date: fechaAplicacion,
        observaciones,
      };
      setVitaminSchedule(prev => [...prev, newVitaminSchedule]);
    }

    alert('Tratamiento registrado y stock actualizado.');
    setTreatmentData({
      animalId: '',
      productId: '',
      dosis: '',
      fechaAplicacion: '',
      observaciones: '',
    });
  };

  const handleSubmitDisease = () => {
    const { animalId, diseaseName, symptoms, diagnosisDate, treatmentProductId, treatmentDosis, treatmentStartDate, treatmentEndDate, recoveryDate, diseaseObservations } = diseaseData;

    if (!animalId || !diseaseName || !diagnosisDate) {
      alert('Por favor, completa los campos obligatorios para el registro de enfermedad.');
      return;
    }

    const productInStock = inventoryItems.find(item => item.id === treatmentProductId);
    if (treatmentProductId && (!productInStock || productInStock.quantity < treatmentDosis)) {
      alert(`No hay suficiente ${productInStock?.name || 'medicamento'} en stock para el tratamiento. Cantidad disponible: ${productInStock?.quantity || 0} ${productInStock?.unit || ''}.`);
      return;
    }

    const newDiseaseRecord = {
      id: `DIS-${Date.now().toString().slice(-5)}`,
      animalId,
      animalName: animals.find(a => a.id === animalId)?.name || 'Desconocido',
      diseaseName,
      symptoms,
      diagnosisDate,
      treatmentProductId,
      treatmentProductName: productInStock?.name || 'N/A',
      treatmentDosis: parseFloat(treatmentDosis) || 0,
      treatmentUnit: productInStock?.unit || 'N/A',
      treatmentStartDate,
      treatmentEndDate,
      recoveryDate,
      diseaseObservations,
    };
    const updatedDiseasesHistory = [...diseasesHistory, newDiseaseRecord];
    setDiseasesHistory(updatedDiseasesHistory);

    // Actualizar el stock del medicamento si se usó
    if (treatmentProductId && treatmentDosis) {
      onUpdateInventoryQuantity(treatmentProductId, parseFloat(treatmentDosis));
    }

    alert('Enfermedad registrada y stock actualizado si aplica.');
    setDiseaseData({
      animalId: '',
      diseaseName: '',
      symptoms: '',
      diagnosisDate: '',
      treatmentProductId: '',
      treatmentDosis: '',
      treatmentStartDate: '',
      treatmentEndDate: '',
      recoveryDate: '',
      diseaseObservations: '',
    });
  };

  // Filtrar productos del inventario que son medicamentos
  const medicalProducts = inventoryItems.filter(item => item.category === 'medicamentos');

  const handleDewormingFrequencyChange = (e) => {
    setDewormingFrequency({ ...dewormingFrequency, value: parseInt(e.target.value) });
  };

  const handleVitaminFrequencyChange = (e) => {
    setVitaminFrequency({ ...vitaminFrequency, value: parseInt(e.target.value) });
  };

  const handleStockThresholdChange = (e) => {
    setStockThreshold(parseInt(e.target.value));
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Control Sanitario con Stock Inteligente</h3>

      {/* Notificaciones */}
      {notifications.length > 0 && (
        <div className="mb-6 p-4 bg-red-100 border border-red-300 rounded-lg text-red-800">
          <h4 className="font-semibold mb-2">Notificaciones Importantes:</h4>
          <ul className="list-disc list-inside">
            {notifications.map((msg, index) => (
              <li key={index}>{msg}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Configuración de Frecuencias y Umbrales */}
      <div className="mb-8 p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Configuración de Frecuencias y Umbrales</h4>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-1">Frecuencia de Desparasitación (meses):</label>
            <input
              type="number"
              min="1"
              className="w-full px-4 py-2 border rounded-lg"
              value={dewormingFrequency.value}
              onChange={handleDewormingFrequencyChange}
            />
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-1">Frecuencia de Vitaminas (meses):</label>
            <input
              type="number"
              min="1"
              className="w-full px-4 py-2 border rounded-lg"
              value={vitaminFrequency.value}
              onChange={handleVitaminFrequencyChange}
            />
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-1">Umbral de Alerta de Stock Bajo:</label>
            <input
              type="number"
              min="0"
              className="w-full px-4 py-2 border rounded-lg"
              value={stockThreshold}
              onChange={handleStockThresholdChange}
            />
          </div>
        </div>
        <p className="text-sm text-gray-600 mt-2">Las vacunas se programan según el tipo de animal y las normativas pecuarias (anual, etc.).</p>
      </div>

      {/* Registro de Tratamientos Generales (Vacunas, Desparasitantes, Vitaminas) */}
      <div className="mb-8 p-4 border rounded-lg bg-gray-50">
        <h4 className="text-lg font-semibold mb-3">Registrar Tratamiento General</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select
            name="animalId"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={treatmentData.animalId}
            onChange={handleInputChange}
          >
            <option value="">Selecciona Animal</option>
            {animals.map(animal => (
              <option key={animal.id} value={animal.id}>{animal.name} ({animal.id})</option>
            ))}
          </select>
          <select
            name="productId"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={treatmentData.productId}
            onChange={handleInputChange}
          >
            <option value="">Selecciona Producto (Medicamento)</option>
            {medicalProducts.map(product => (
              <option key={product.id} value={product.id}>
                {product.name} (Stock: {product.quantity} {product.unit})
              </option>
            ))}
          </select>
          <input
            type="number"
            name="dosis"
            placeholder="Dosis Aplicada"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={treatmentData.dosis}
            onChange={handleInputChange}
          />
          <input
            type="date"
            name="fechaAplicacion"
            placeholder="Fecha de Aplicación"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={treatmentData.fechaAplicacion}
            onChange={handleInputChange}
          />
          <textarea
            name="observaciones"
            placeholder="Observaciones (ej. Lote, Reacción)"
            rows="2"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black col-span-full"
            value={treatmentData.observaciones}
            onChange={handleInputChange}
          ></textarea>
        </div>
        <button
          onClick={handleSubmitTreatment}
          className="mt-6 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
        >
          Registrar Tratamiento
        </button>
      </div>

      {/* Registro de Enfermedades */}
      <div className="mb-8 p-4 border rounded-lg bg-red-50">
        <h4 className="text-lg font-semibold mb-3">Registrar Enfermedad</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <select
            name="animalId"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.animalId}
            onChange={handleDiseaseInputChange}
          >
            <option value="">Selecciona Animal</option>
            {animals.map(animal => (
              <option key={animal.id} value={animal.id}>{animal.name} ({animal.id})</option>
            ))}
          </select>
          <input
            type="text"
            name="diseaseName"
            placeholder="Nombre de la Enfermedad"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.diseaseName}
            onChange={handleDiseaseInputChange}
          />
          <textarea
            name="symptoms"
            placeholder="Síntomas"
            rows="2"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black col-span-full"
            value={diseaseData.symptoms}
            onChange={handleDiseaseInputChange}
          ></textarea>
          <input
            type="date"
            name="diagnosisDate"
            placeholder="Fecha de Diagnóstico"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.diagnosisDate}
            onChange={handleDiseaseInputChange}
          />
          <select
            name="treatmentProductId"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.treatmentProductId}
            onChange={handleDiseaseInputChange}
          >
            <option value="">Medicamento para Tratamiento (Opcional)</option>
            {medicalProducts.map(product => (
              <option key={product.id} value={product.id}>
                {product.name} (Stock: {product.quantity} {product.unit})
              </option>
            ))}
          </select>
          <input
            type="number"
            name="treatmentDosis"
            placeholder="Dosis del Tratamiento"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.treatmentDosis}
            onChange={handleDiseaseInputChange}
          />
          <input
            type="date"
            name="treatmentStartDate"
            placeholder="Inicio Tratamiento"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-2 focus:ring-black"
            value={diseaseData.treatmentStartDate}
            onChange={handleDiseaseInputChange}
          />
          <input
            type="date"
            name="treatmentEndDate"
            placeholder="Fin Tratamiento (Opcional)"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.treatmentEndDate}
            onChange={handleDiseaseInputChange}
          />
          <input
            type="date"
            name="recoveryDate"
            placeholder="Fecha de Recuperación (Opcional)"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
            value={diseaseData.recoveryDate}
            onChange={handleDiseaseInputChange}
          />
          <textarea
            name="diseaseObservations"
            placeholder="Observaciones de la Enfermedad"
            rows="2"
            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black col-span-full"
            value={diseaseData.diseaseObservations}
            onChange={handleDiseaseInputChange}
          ></textarea>
        </div>
        <button
          onClick={handleSubmitDisease}
          className="mt-6 bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700 transition-colors"
        >
          Registrar Enfermedad
        </button>
      </div>

      {/* Historial de Tratamientos */}
      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Historial de Tratamientos</h4>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="py-2 px-4 border">ID Animal</th>
                <th className="py-2 px-4 border">Producto</th>
                <th className="py-2 px-4 border">Dosis</th>
                <th className="py-2 px-4 border">Fecha</th>
                <th className="py-2 px-4 border">Observaciones</th>
              </tr>
            </thead>
            <tbody>
              {treatmentsHistory.map((treatment, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="py-2 px-4 border">{treatment.animalId}</td>
                  <td className="py-2 px-4 border">{treatment.productName}</td>
                  <td className="py-2 px-4 border">{treatment.dosis} {treatment.unit}</td>
                  <td className="py-2 px-4 border">{treatment.fechaAplicacion}</td>
                  <td className="py-2 px-4 border">{treatment.observaciones || 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Historial de Enfermedades */}
      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Historial de Enfermedades</h4>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="py-2 px-4 border">ID Animal</th>
                <th className="py-2 px-4 border">Enfermedad</th>
                <th className="py-2 px-4 border">Diagnóstico</th>
                <th className="py-2 px-4 border">Tratamiento</th>
                <th className="py-2 px-4 border">Recuperación</th>
                <th className="py-2 px-4 border">Observaciones</th>
              </tr>
            </thead>
            <tbody>
              {diseasesHistory.map((disease, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="py-2 px-4 border">{disease.animalId}</td>
                  <td className="py-2 px-4 border">{disease.diseaseName}</td>
                  <td className="py-2 px-4 border">{disease.diagnosisDate}</td>
                  <td className="py-2 px-4 border">{disease.treatmentProductName} ({disease.treatmentDosis} {disease.treatmentUnit})</td>
                  <td className="py-2 px-4 border">{disease.recoveryDate || 'N/A'}</td>
                  <td className="py-2 px-4 border">{disease.diseaseObservations || 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Calendario de Vacunación */}
      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Calendario de Vacunación</h4>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="py-2 px-4 border">ID Animal</th>
                <th className="py-2 px-4 border">Nombre Animal</th>
                <th className="py-2 px-4 border">Vacuna</th>
                <th className="py-2 px-4 border">Fecha</th>
                <th className="py-2 px-4 border">Observaciones</th>
              </tr>
            </thead>
            <tbody>
              {vaccinationSchedule.map((schedule, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="py-2 px-4 border">{schedule.animalId}</td>
                  <td className="py-2 px-4 border">{schedule.animalName}</td>
                  <td className="py-2 px-4 border">{schedule.vaccineName}</td>
                  <td className="py-2 px-4 border">{schedule.date}</td>
                  <td className="py-2 px-4 border">{schedule.observaciones || 'N/A'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Calendario de Desparasitación */}
      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Calendario de Desparasitación</h4>
        <div className="overflow-x-auto">
