import React, { useState } from 'react';

const AuthRegisterForm = ({ onRegisterSuccess, onNavigateToLogin }) => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [farmName, setFarmName] = useState('');
  const [adminName, setAdminName] = useState('');
  const [error, setError] = useState('');

  const handleRegister = () => {
    setError(''); // Limpiar errores previos

    if (!fullName || !email || !password || !farmName) {
      setError('Por favor, completa todos los campos obligatorios.');
      return;
    }

    // Simulación de registro: pasar los datos al App.js para que los guarde
    // La validación de email duplicado se hará en App.js
    const success = onRegisterSuccess({ fullName, email, password, farmName, adminName });
    if (success) {
      // Si el registro fue exitoso (validado en App.js), se navega al login
      // El alert de éxito se maneja en App.js
    } else {
      // Si no fue exitoso (ej. email duplicado), el error ya se mostró en App.js
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-center mb-6">Registro de Usuario</h2>
      {error && <p className="text-red-500 text-center mb-4">{error}</p>}
      <input
        type="text"
        placeholder="Nombre Completo"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={fullName}
        onChange={(e) => setFullName(e.target.value)}
      />
      <input
        type="email"
        placeholder="Correo Electrónico"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Contraseña"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <input
        type="text"
        placeholder="Nombre de la Finca"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={farmName}
        onChange={(e) => setFarmName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Nombre del Administrador (Opcional)"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={adminName}
        onChange={(e) => setAdminName(e.target.value)}
      />
      <button
        onClick={handleRegister}
        className="w-full mt-6 bg-black text-white py-2 rounded-lg hover:bg-gray-800 transition-colors"
      >
        Registrarse
      </button>
      <p className="text-center mt-4 text-gray-600">
        ¿Ya tienes cuenta?{' '}
        <button onClick={onNavigateToLogin} className="text-black font-semibold hover:underline">
          Inicia Sesión
        </button>
      </p>
    </div>
  );
};

export default AuthRegisterForm;
