import React, { useState } from 'react';

const AuthLoginForm = ({ onLoginSuccess, onNavigateToRegister, registeredUsers }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = () => {
    setError(''); // Limpiar errores previos
    const user = registeredUsers.find(u => u.email === email);

    if (!user) {
      setError('Correo no registrado.');
      return;
    }

    // Simulación de verificación de contraseña (en un entorno real, se compararía un hash)
    if (user.password !== password) { // Asumiendo que la contraseña se guarda en texto plano para esta simulación
      setError('Contraseña incorrecta.');
      return;
    }

    onLoginSuccess(user);
  };

  return (
    <div>
      <h2 className="text-2xl font-bold text-center mb-6">Iniciar Sesión</h2>
      {error && <p className="text-red-500 text-center mb-4">{error}</p>}
      <input
        type="email"
        placeholder="Correo Electrónico"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Contraseña"
        className="w-full mt-3 px-4 py-2 bg-white border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-black transition"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button
        onClick={handleLogin}
        className="w-full mt-6 bg-black text-white py-2 rounded-lg hover:bg-gray-800 transition-colors"
      >
        Iniciar Sesión
      </button>
      <p className="text-center mt-4 text-gray-600">
        ¿No tienes cuenta?{' '}
        <button onClick={onNavigateToRegister} className="text-black font-semibold hover:underline">
          Regístrate
        </button>
      </p>
    </div>
  );
};

export default AuthLoginForm;
