import React, { useState } from 'react';

const GESTATION_DAYS = 283; // Días promedio de gestación para bovinos

const ReproductiveControlPage = ({ animals, onUpdateAnimalProduction, onRegisterAnimal,onRegisterReproductiveEvent, onDeletePregnantCow }) => {
  const [currentSection, setCurrentSection] = useState('montaNatural'); // 'montaNatural', 'inseminacion', 'gestacion'

  const [montaNaturalData, setMontaNaturalData] = useState({
    vacaId: '',
    toroId: '',
    fechaMonta: '',
    observaciones: '',
  });

  const [inseminacionData, setInseminacionData] = useState({
    vacaId: '',
    semenId: '', // ID del semen utilizado
    fechaInseminacion: '',
    inseminador: '',
    observaciones: '',
  });

  const [gestacionData, setGestacionData] = useState({
    vacaId: '',
    fechaDiagnostico: '',
    resultadoDiagnostico: '', // 'positiva', 'negativa'
    fechaPartoEstimada: '', // Calculada
    fechaPartoReal: '',
    pesoTernero: '',
    sexoTernero: '',
    nombreTernero: '',
    observacionesParto: '',
  });

  const pregnantCows = animals.filter(animal => animal.estadoReproductivo === 'preñada');


  const handleMontaNaturalChange = (e) => {
    const { name, value } = e.target;
    setMontaNaturalData({ ...montaNaturalData, [name]: value });
  };

  const handleInseminacionChange = (e) => {
    const { name, value } = e.target;
    setInseminacionData({ ...inseminacionData, [name]: value });
  };

  const handleGestacionChange = (e) => {
    const { name, value } = e.target;
    setGestacionData({ ...gestacionData, [name]: value });
  };

  const handleMontaNaturalSubmit = () => {
  const { vacaId, toroId, fechaMonta, observaciones } = montaNaturalData;

  if (!vacaId || !toroId || !fechaMonta) {
    alert('Completa los campos obligatorios de Monta Natural.');
    return;
  }

  onUpdateAnimalProduction(vacaId, {
    estadoReproductivo: 'preñada',
    fechaUltimaGestacion: fechaMonta,
  });

  onRegisterReproductiveEvent({
    idAnimal: vacaId,
    tipoEvento: 'monta',
    fechaEvento: fechaMonta,
    observaciones,
    estadoPosterior: 'preñada',
    toroId
  });

  alert('Monta natural registrada.');
  setMontaNaturalData({ vacaId: '', toroId: '', fechaMonta: '', observaciones: '' });
};
  const handleInseminacionSubmit = () => {
  const { vacaId, semenId, fechaInseminacion, observaciones } = inseminacionData;

  if (!vacaId || !semenId || !fechaInseminacion) {
    alert('Completa todos los campos de inseminación.');
    return;
  }

  // ✅ Actualizar estado de la vaca
  onUpdateAnimalProduction(vacaId, {
    estadoReproductivo: 'preñada',
    fechaUltimaGestacion: fechaInseminacion,
  });

  // ✅ Registrar evento reproductivo
  onRegisterReproductiveEvent({
    idAnimal: vacaId,
    tipoEvento: 'inseminacion',
    fechaEvento: fechaInseminacion,
    observaciones,
    estadoPosterior: 'preñada',
    toroId: semenId, // opcional
  });

  alert('Inseminación registrada correctamente.');

  // ✅ Limpiar el formulario
  setInseminacionData({
    vacaId: '',
    semenId: '',
    fechaInseminacion: '',
    observaciones: '',
  });
};

  const handleGestacionSubmit = () => {
  const {
    vacaId,
    fechaDiagnostico,
    resultadoDiagnostico,
    fechaPartoEstimada,
    pesoTernero,
    sexoTernero,
    nombreTernero,
    observacionesParto
  } = gestacionData;

  if (!vacaId || !fechaDiagnostico || !resultadoDiagnostico) {
    alert('Completa los campos obligatorios del diagnóstico de gestación.');
    return;
  }

  onUpdateAnimalProduction(vacaId, {
    estadoReproductivo: 'preñada',
    fechaUltimaGestacion: fechaDiagnostico,
  });

  onRegisterReproductiveEvent({
    idAnimal: vacaId,
    tipoEvento: 'diagnóstico',
    fechaEvento: fechaDiagnostico,
    observaciones: observacionesParto,
    estadoPosterior: 'preñada',
  });

if (gestacionData.fechaPartoReal) {
  onUpdateAnimalProduction(vacaId, {
    estadoReproductivo: 'producción',
    fechaUltimoParto: gestacionData.fechaPartoReal,
  });

  onRegisterReproductiveEvent({
    idAnimal: vacaId,
    tipoEvento: 'parto',
    fechaEvento: gestacionData.fechaPartoReal,
    observaciones: observacionesParto,
    estadoPosterior: 'producción',
  });
};

  alert('Diagnóstico de gestación registrado.');
  setGestacionData({
    vacaId: '',
    fechaDiagnostico: '',
    resultadoDiagnostico: '',
    fechaPartoEstimada: '',
    pesoTernero: '',
    sexoTernero: '',
    nombreTernero: '',
    observacionesParto: '',
  });
};

  return (
     <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Control Reproductivo</h3>

      <div className="flex justify-center mb-6 space-x-4">
        <button
          onClick={() => setCurrentSection('montaNatural')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'montaNatural' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Monta Natural
        </button>
        <button
          onClick={() => setCurrentSection('inseminacion')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'inseminacion' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Inseminación
        </button>
        <button
          onClick={() => setCurrentSection('gestacion')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'gestacion' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Control de Gestación / Parto
        </button>
      </div>

      {currentSection === 'montaNatural' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Registro de Monta Natural</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <label className="block text-gray-700 text-sm font-bold mb-1">Vaca:</label>
            <select
              name="vacaId"
              className="w-full px-4 py-2 border rounded-lg"
              value={montaNaturalData.vacaId}
              onChange={handleMontaNaturalChange}
            >
              <option value="">Selecciona Vaca</option>
              {animals.filter(a => a.sexo === 'hembra').map(vaca => (
                <option key={vaca.id} value={vaca.id}>{vaca.name} ({vaca.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">Toro:</label>
            <select
              name="toroId"
              className="w-full px-4 py-2 border rounded-lg"
              value={montaNaturalData.toroId}
              onChange={handleMontaNaturalChange}
            >
              <option value="">Selecciona Toro</option>
              {animals.filter(a => a.sexo === 'macho').map(toro => (
                <option key={toro.id} value={toro.id}>{toro.name} ({toro.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Monta:</label>
            <input
              type="date"
              name="fechaMonta"
              placeholder="Fecha de Monta"
              className="w-full px-4 py-2 border rounded-lg"
              value={montaNaturalData.fechaMonta}
              onChange={handleMontaNaturalChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones:</label>
            <textarea
              name="observaciones"
              placeholder="Observaciones"
              rows="2"
              className="w-full px-4 py-2 border rounded-lg col-span-full"
              value={montaNaturalData.observaciones}
              onChange={handleMontaNaturalChange}
            ></textarea>
          </div>
          <button
            onClick={handleMontaNaturalSubmit}
            className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
          >
            Registrar Monta
          </button>
        </div>
      )}

      {currentSection === 'inseminacion' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Registro de Inseminación</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <label className="block text-gray-700 text-sm font-bold mb-1">Vaca:</label>
            <select
              name="vacaId"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.vacaId}
              onChange={handleInseminacionChange}
            >
              <option value="">Selecciona Vaca</option>
              {animals.filter(a => a.sexo === 'hembra').map(vaca => (
                <option key={vaca.id} value={vaca.id}>{vaca.name} ({vaca.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">ID Semen Utilizado:</label>
            <input
              type="text"
              name="semenId"
              placeholder="ID Semen Utilizado"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.semenId}
              onChange={handleInseminacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Inseminación:</label>
            <input
              type="date"
              name="fechaInseminacion"
              placeholder="Fecha de Inseminación"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.fechaInseminacion}
              onChange={handleInseminacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1">Inseminador:</label>
            <input
              type="text"
              name="inseminador"
              placeholder="Inseminador (Opcional)"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.inseminador}
              onChange={handleInseminacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones:</label>
            <textarea
              name="observaciones"
              placeholder="Observaciones"
              rows="2"
              className="w-full px-4 py-2 border rounded-lg col-span-full"
              value={inseminacionData.observaciones}
              onChange={handleInseminacionChange}
            ></textarea>
          </div>
          <button
            onClick={handleInseminacionSubmit}
            className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
          >
            Registrar Inseminación
          </button>
        </div>
      )}

      {currentSection === 'gestacion' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Control de Gestación / Parto</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <label className="block text-gray-700 text-sm font-bold mb-1">Vaca:</label>
            <select
              name="vacaId"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.vacaId}
              onChange={handleGestacionChange}
            >
              <option value="">Selecciona Vaca</option>
              {animals.filter(a => a.sexo === 'hembra').map(vaca => (
                <option key={vaca.id} value={vaca.id}>{vaca.name} ({vaca.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha Diagnóstico Gestación:</label>
            <input
              type="date"
              name="fechaDiagnostico"
              placeholder="Fecha de Diagnóstico de Gestación"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.fechaDiagnostico}
              onChange={handleGestacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1">Resultado Diagnóstico:</label>
            <select
              name="resultadoDiagnostico"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.resultadoDiagnostico}
              onChange={handleGestacionChange}
            >
              <option value="">Resultado Diagnóstico</option>
              <option value="positiva">Positiva (Preñada)</option>
              <option value="negativa">Negativa</option>
            </select>
            {gestacionData.resultadoDiagnostico === 'positiva' && (
              <>
                <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Parto Estimada:</label>
                <input
                  type="date"
                  name="fechaPartoEstimada"
                  placeholder="Fecha de Parto Estimada"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.fechaPartoEstimada}
                  onChange={handleGestacionChange}
                  title="Calculada automáticamente al registrar monta/inseminación o diagnóstico positivo"
                />
              </>
            )}
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Parto Real (si ya ocurrió):</label>
            <input
              type="date"
              name="fechaPartoReal"
              placeholder="Fecha de Parto Real (si ya ocurrió)"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.fechaPartoReal}
              onChange={handleGestacionChange}
            />
            {gestacionData.fechaPartoReal && (
              <>
                <label className="block text-gray-700 text-sm font-bold mb-1">Peso del Ternero (kg):</label>
                <input
                  type="number"
                  name="pesoTernero"
                  placeholder="Peso del Ternero (kg)"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.pesoTernero}
                  onChange={handleGestacionChange}
                />
                <label className="block text-gray-700 text-sm font-bold mb-1">Sexo del Ternero:</label>
                <select
                  name="sexoTernero"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.sexoTernero}
                  onChange={handleGestacionChange}
                >
                  <option value="">Sexo del Ternero</option>
                  <option value="macho">Macho</option>
                  <option value="hembra">Hembra</option>
                </select>
                <label className="block text-gray-700 text-sm font-bold mb-1">Nombre del Ternero:</label>
                <input
                  type="text"
                  name="nombreTernero"
                  placeholder="Nombre del Ternero"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.nombreTernero}
                  onChange={handleGestacionChange}
                />
              </>
            )}
            <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones del Parto:</label>
            <textarea
              name="observacionesParto"
              placeholder="Observaciones del Parto"
              rows="2"
              className="w-full px-4 py-2 border rounded-lg col-span-full"
              value={gestacionData.observacionesParto}
              onChange={handleGestacionChange}
            ></textarea>
          </div>
          <button
            onClick={handleGestacionSubmit}
            className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
          >
            Registrar Gestación / Parto
          </button>
        </div>
      )}

    {/* Calendario de Gestación y Vacas Preñadas */}
    <div className="mt-8 pt-4 border-t border-gray-200">
      <h4 className="text-lg font-semibold mb-3">Vacas Preñadas y Calendario de Gestación</h4>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-white border text-sm">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4 border">ID Vaca</th>
              <th className="py-2 px-4 border">Nombre</th>
              <th className="py-2 px-4 border">Fecha Monta/Insem.</th>
              <th className="py-2 px-4 border">Fecha Parto Estimada</th>
              <th className="py-2 px-4 border">Días Restantes</th>
            </tr>
          </thead>
          <tbody>
           {pregnantCows.map(vaca => {
           const fechaMonta = new Date(vaca.fechaUltimaGestacion);
           const fechaParto = new Date(fechaMonta);
           fechaParto.setDate(fechaParto.getDate() + 283);
           const hoy = new Date();
           const diasRestantes = Math.ceil((fechaParto - hoy) / (1000 * 60 * 60 * 24));

        return (
          <tr key={vaca.id}>
            <td className="py-2 px-4 border">{vaca.id}</td>
            <td className="py-2 px-4 border">{vaca.name}</td>
            <td className="py-2 px-4 border">{vaca.fechaUltimaGestacion || 'N/A'}</td>
            <td className="py-2 px-4 border">{fechaParto.toISOString().split('T')[0]}</td>
            <td className="py-2 px-4 border">{diasRestantes >= 0 ? diasRestantes : 'Venció'}</td>
            <td className="py-2 px-4 border">
              <button
                className="text-red-600 hover:underline"
                onClick={() => onDeletePregnantCow(vaca.id)}
              >
                Eliminar
              </button>
             </td>
           </tr>
           );
        })}
      </tbody>
    </table>
   </div>
  </div>
 </div>
 );
 };

  export default ReproductiveControlPage;
