import React from 'react';

const AuthLayout = ({ children }) => {
  return (
    <div 
      className="min-h-screen flex items-center justify-center p-4 bg-cover bg-center"
      style={{ 
        backgroundImage: "url('https://images.unsplash.com/photo-1546527868-ccb7ee7dfa6a?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D')",
        // Puedes cambiar la URL de la imagen por una que te guste más.
        // Esta imagen incluye varias razas de ganado como Gyr, Girolando, Holstein, Guzerat, Pardo Suizo, etc.
      }}
    >
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md bg-opacity-90 backdrop-filter backdrop-blur-sm">
        {children}
      </div>
    </div>
  );
};

export default AuthLayout;

