import React, { useState, useEffect, useRef } from 'react';
import { classifyAnimalAge } from '../utils/helpers'; // Importar la función de utilidad
import { exportToCsv, exportTableToPdf } from '../utils/exportHelpers'; // Importar funciones de exportación

const GESTATION_DAYS = 283; // Días promedio de gestación para bovinos

const ReproductiveControlPage = ({ animals, onUpdateAnimalProduction, onRegisterReproductiveEvent, onDeletePregnantCow, reproductiveRecords, setReproductiveRecords, onRegisterAnimal, currentUser, logoUrl }) => {
  const [currentSection, setCurrentSection] = useState('montaNatural'); // 'montaNatural', 'inseminacion', 'gestacion', 'historial'

  const [montaNaturalData, setMontaNaturalData] = useState({
    vacaId: '',
    toroId: '',
    fechaMonta: '',
    observaciones: '',
  });

  const [inseminacionData, setInseminacionData] = useState({
    vacaId: '',
    semenId: '', // ID del semen utilizado
    fechaInseminacion: '',
    inseminador: '',
    observaciones: '',
  });

  const [gestacionData, setGestacionData] = useState({
    vacaId: '',
    fechaDiagnostico: '',
    resultadoDiagnostico: '', // 'positiva', 'negativa'
    fechaPartoEstimada: '', // Calculada
    fechaPartoReal: '',
    pesoTernero: '',
    sexoTernero: '',
    nombreTernero: '',
    observacionesParto: '',
    padreTerneroId: '', // ID del padre si está registrado
    padreTerneroNombre: '', // Nombre del padre si es nuevo
    razaPadreTernero: '', // Raza del padre si es nuevo
    isPadreRegistered: false, // Para controlar si el padre está registrado
  });

  const [editingRecord, setEditingRecord] = useState(null); // To hold the record being edited
  const historyTableRef = useRef(null); // Ref for the history table

  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');

  // Función auxiliar para calcular días restantes
  const calcularDiasRestantes = (fechaPartoEstimada) => {
    const hoy = new Date();
    const fecha = new Date(fechaPartoEstimada);
    // Verificar si la fecha es válida antes de calcular la diferencia
    if (isNaN(fecha.getTime())) {
      return NaN; // Retorna NaN si la fecha no es válida
    }
    const diferencia = fecha.getTime() - hoy.getTime(); // Usar getTime() para operar con milisegundos
    return Math.ceil(diferencia / (1000 * 60 * 60 * 24));
  };

  // useEffect para evaluar registros reproductivos y actualizar estados
  useEffect(() => {
    if (!reproductiveRecords || reproductiveRecords.length === 0) {
      return;
    }

    let animalStatusUpdates = [];
    let recordStatusUpdates = [];

    reproductiveRecords.forEach(record => {
      const currentAnimal = animals.find(a => a.id === record.idAnimal);
      if (!currentAnimal) return; // Animal not found, skip

      // Logic for 'preñada' records that are not yet finalized
      if (record.tipoEvento === 'diagnóstico' && record.estadoPosterior === 'preñada' && record.estado !== 'finalizado') {
        let estimatedBirthDateStr = record.fechaPartoEstimada;

        // If estimated birth date is missing, calculate it from the diagnosis date
        if (!estimatedBirthDateStr) {
          const diagnosisDate = new Date(record.fechaEvento);
          if (diagnosisDate instanceof Date && !isNaN(diagnosisDate.getTime())) {
            const estimatedDate = new Date(diagnosisDate);
            estimatedDate.setDate(estimatedDate.getDate() + GESTATION_DAYS);
            estimatedBirthDateStr = estimatedDate.toISOString().split('T')[0];
            // Update the record itself with the estimated date for persistence
            recordStatusUpdates.push({ id: record.id, update: { fechaPartoEstimada: estimatedBirthDateStr } });
          }
        }

        if (estimatedBirthDateStr) {
          const dias = calcularDiasRestantes(estimatedBirthDateStr);

          // Solo aplicar reglas si los días restantes son un número válido
          if (!isNaN(dias)) {
            // Rule 1: Change to SECA if less than 100 days to birth AND animal is not already 'seca'
            if (dias <= 100 && dias > 0 && currentAnimal.estadoReproductivo !== 'seca') {
              animalStatusUpdates.push({ id: record.idAnimal, update: { estadoReproductivo: 'seca' } });
            }

            // Rule 2: Change to PRODUCCIÓN if estimated birth date passed AND animal is not already 'produccion'
            if (dias <= 0 && currentAnimal.estadoReproductivo !== 'produccion') {
              animalStatusUpdates.push({ id: record.idAnimal, update: { estadoReproductivo: 'produccion', fechaUltimoParto: estimatedBirthDateStr } });
              // Mark the reproductive record as finalized after birth
              recordStatusUpdates.push({ id: record.id, update: { estado: 'finalizado' } });
            }
          }
        }
      }
    });

    // Apply animal status updates
    animalStatusUpdates.forEach(update => {
      onUpdateAnimalProduction(update.id, update.update);
    });

    // Apply reproductive record status updates
    if (recordStatusUpdates.length > 0) {
      const newReproductiveRecords = reproductiveRecords.map(rec => {
        const update = recordStatusUpdates.find(u => u.id === rec.id);
        return update ? { ...rec, ...update.update } : rec;
      });
      setReproductiveRecords(newReproductiveRecords);
    }

  }, [reproductiveRecords, animals, onUpdateAnimalProduction, setReproductiveRecords]); // Dependencies

  const handleMontaNaturalChange = (e) => {
    const { name, value } = e.target;
    setMontaNaturalData({ ...montaNaturalData, [name]: value });
  };

  const handleInseminacionChange = (e) => {
    const { name, value } = e.target;
    setInseminacionData({ ...inseminacionData, [name]: value });
  };

  const handleGestacionChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === 'checkbox') {
      setGestacionData({ ...gestacionData, [name]: checked });
    } else {
      setGestacionData({ ...gestacionData, [name]: value });
    }
  };

  const handleMontaNaturalSubmit = () => {
    const { vacaId, toroId, fechaMonta, observaciones } = montaNaturalData;

    if (!vacaId || !toroId || !fechaMonta) {
      alert('¡Ups! Parece que te faltan algunos datos para registrar la monta natural. ¡Completa todos los campos y lo intentamos de nuevo!');
      return;
    }

    // Calcular fecha de parto estimada para el registro
    const montaDateObj = new Date(fechaMonta);
    let estimatedBirthDateForRecord = '';
    if (montaDateObj instanceof Date && !isNaN(montaDateObj.getTime())) {
      const estimatedDate = new Date(montaDateObj);
      estimatedDate.setDate(estimatedDate.getDate() + GESTATION_DAYS);
      estimatedBirthDateForRecord = estimatedDate.toISOString().split('T')[0];
    }

    onRegisterReproductiveEvent({
      id: `REC-${Date.now().toString().slice(-5)}`, // Generar ID único
      idAnimal: vacaId,
      tipoEvento: 'monta',
      fechaEvento: fechaMonta,
      observaciones,
      estadoPosterior: 'posiblemente preñada', // Estado inicial después de monta/inseminación
      toroId,
      fechaPartoEstimada: estimatedBirthDateForRecord, // Guardar fecha de parto estimada
    });

    alert('¡Excelente! La monta natural ha sido registrada con éxito. ¡Esperemos que pronto tengamos buenas noticias!');
    setMontaNaturalData({ vacaId: '', toroId: '', fechaMonta: '', observaciones: '' });
  };

  const handleInseminacionSubmit = () => {
    const { vacaId, semenId, fechaInseminacion, observaciones } = inseminacionData;

    if (!vacaId || !semenId || !fechaInseminacion) {
      alert('¡Uy! Para registrar la inseminación, necesitamos que llenes todos los campos. ¡No te rindas, ya casi lo logras!');
      return;
    }

    // Calcular fecha de parto estimada para el registro
    const inseminacionDateObj = new Date(fechaInseminacion);
    let estimatedBirthDateForRecord = '';
    if (inseminacionDateObj instanceof Date && !isNaN(inseminacionDateObj.getTime())) {
      const estimatedDate = new Date(inseminacionDateObj);
      estimatedDate.setDate(estimatedDate.getDate() + GESTATION_DAYS);
      estimatedBirthDateForRecord = estimatedDate.toISOString().split('T')[0];
    }

    onRegisterReproductiveEvent({
      id: `REC-${Date.now().toString().slice(-5)}`, // Generar ID único
      idAnimal: vacaId,
      tipoEvento: 'inseminacion',
      fechaEvento: fechaInseminacion,
      observaciones,
      estadoPosterior: 'posiblemente preñada', // Estado inicial después de monta/inseminación
      toroId: semenId, // opcional
      fechaPartoEstimada: estimatedBirthDateForRecord, // Guardar fecha de parto estimada
    });

    alert('¡Genial! La inseminación ha sido registrada. ¡Crucemos los dedos por un buen resultado!');

    setInseminacionData({
      vacaId: '',
      semenId: '',
      fechaInseminacion: '',
      observaciones: '',
    });
  };

  const handleGestacionSubmit = () => {
    const {
      vacaId,
      fechaDiagnostico,
      resultadoDiagnostico,
      pesoTernero,
      sexoTernero,
      nombreTernero,
      observacionesParto,
      fechaPartoReal,
      padreTerneroId,
      padreTerneroNombre,
      razaPadreTernero,
      isPadreRegistered
    } = gestacionData;

    if (!vacaId || !fechaDiagnostico || !resultadoDiagnostico) {
      alert('¡Atención! Para el diagnóstico de gestación, necesitamos que completes los campos obligatorios. ¡Vamos, tú puedes!');
      return;
    }

    let estimatedBirthDateForRecord = '';
    let newAnimalState = '';

    if (resultadoDiagnostico === 'positiva') {
      newAnimalState = 'preñada';
      const diagnosisDateObj = new Date(fechaDiagnostico);
      if (diagnosisDateObj instanceof Date && !isNaN(diagnosisDateObj.getTime())) {
        const estimatedDate = new Date(diagnosisDateObj);
        estimatedDate.setDate(estimatedDate.getDate() + GESTATION_DAYS);
        estimatedBirthDateForRecord = estimatedDate.toISOString().split('T')[0];
      }
    } else {
      newAnimalState = 'vacia';
    }

    onUpdateAnimalProduction(vacaId, {
      estadoReproductivo: newAnimalState,
      fechaUltimaGestacion: newAnimalState === 'preñada' ? fechaDiagnostico : '',
    });

    onRegisterReproductiveEvent({
      id: `REC-${Date.now().toString().slice(-5)}`,
      idAnimal: vacaId,
      tipoEvento: 'diagnóstico',
      fechaEvento: fechaDiagnostico,
      observaciones: `Diagnóstico: ${resultadoDiagnostico}. ${observacionesParto}`,
      estadoPosterior: newAnimalState,
      fechaPartoEstimada: estimatedBirthDateForRecord,
    });

    if (fechaPartoReal) {
      onUpdateAnimalProduction(vacaId, {
        estadoReproductivo: 'produccion',
        fechaUltimoParto: fechaPartoReal,
      });

      onRegisterReproductiveEvent({
        id: `REC-${Date.now().toString().slice(-5)}`,
        idAnimal: vacaId,
        tipoEvento: 'parto',
        fechaEvento: fechaPartoReal,
        observaciones: observacionesParto,
        estadoPosterior: 'produccion',
      });

      if (nombreTernero && sexoTernero && pesoTernero) {
        const birthYear = new Date(fechaPartoReal).getFullYear().toString().slice(-2);
        const newCalfId = `${vacaId}-${birthYear}`; 

        const newCalf = {
          id: newCalfId,
          name: nombreTernero,
          raza: animals.find(a => a.id === vacaId)?.raza || 'Desconocida',
          peso: pesoTernero,
          sexo: sexoTernero,
          fechaNacimiento: fechaPartoReal,
          fechaCompra: '',
          tipoProduccion: '',
          idpadre: isPadreRegistered ? padreTerneroId : padreTerneroNombre,
          razaPadre: isPadreRegistered ? animals.find(a => a.id === padreTerneroId)?.raza || 'Desconocida' : razaPadreTernero,
          idmadre: vacaId,
          estadoReproductivo: 'desarrollo',
          edadClasificada: classifyAnimalAge(fechaPartoReal, sexoTernero),
        };
        onRegisterAnimal(newCalf);
        alert(`¡Felicidades! El ternero ${nombreTernero} ha sido registrado automáticamente con ID: ${newCalfId}. ¡Un nuevo miembro en la familia!`);
      }
    }

    alert('¡Diagnóstico de gestación registrado! Mantente al tanto de las novedades.');
    setGestacionData({
      vacaId: '',
      fechaDiagnostico: '',
      resultadoDiagnostico: '',
      fechaPartoEstimada: '',
      pesoTernero: '',
      sexoTernero: '',
      nombreTernero: '',
      observacionesParto: '',
      fechaPartoReal: '',
      padreTerneroId: '',
      padreTerneroNombre: '',
      razaPadreTernero: '',
      isPadreRegistered: false,
    });
  };

  const handleDeleteHistoryRecord = (recordId) => {
    if (window.confirm('¿Estás seguro de que quieres eliminar este registro? ¡Esta acción no se puede deshacer!')) {
      const updatedRecords = reproductiveRecords.filter(record => record.id !== recordId);
      setReproductiveRecords(updatedRecords); // Update state in App.js via prop
      alert('¡Registro eliminado! Como si nunca hubiera existido.');
    }
  };

  const startEditingHistoryRecord = (record) => {
    setEditingRecord({ ...record }); // Create a copy to edit
    alert('¡A editar se ha dicho! Modifica los campos y guarda los cambios.');
  };

  const handleEditingHistoryChange = (e) => {
    const { name, value } = e.target;
    setEditingRecord(prev => ({ ...prev, [name]: value }));
  };

  const saveEditedHistoryRecord = () => {
    const updatedRecords = reproductiveRecords.map(record =>
      record.id === editingRecord.id ? editingRecord : record
    );
    setReproductiveRecords(updatedRecords); // Update state in App.js via prop
    setEditingRecord(null); // Exit editing mode
    alert('¡Cambios guardados! Tu historial está al día.');
  };

  const cancelEditingHistoryRecord = () => {
    setEditingRecord(null); // Exit editing mode
    alert('¡Edición cancelada! No te preocupes, tus datos están a salvo.');
  };

  // Function to add a manual reproductive event
  const handleAddManualEvent = () => {
    const animalId = prompt("Ingresa el ID del animal para el evento manual:");
    const tipoEvento = prompt("¿Qué tipo de evento quieres registrar? (ej. 'observacion', 'tratamiento'):");
    const fechaEvento = prompt("¿Cuándo ocurrió el evento? (YYYY-MM-DD):");
    const observaciones = prompt("¿Alguna observación adicional sobre este evento?:");
    const estadoPosterior = prompt("¿Cuál es el estado del animal después de este evento? (ej. 'vacia', 'produccion'):");

    if (animalId && tipoEvento && fechaEvento) {
      onRegisterReproductiveEvent({
        id: `REC-${Date.now().toString().slice(-5)}`, // Generar ID único
        idAnimal: animalId,
        tipoEvento: tipoEvento,
        fechaEvento: fechaEvento,
        observaciones: observaciones,
        estadoPosterior: estadoPosterior,
      });
      alert('¡Evento manual registrado! Tu historial sigue creciendo.');
    } else {
      alert('¡Ojo! Para registrar un evento manual, necesitamos el ID del animal, el tipo de evento y la fecha. ¡Inténtalo de nuevo!');
    }
  };

  const handleExportHistory = async (format) => {
    if (reproductiveRecords.length === 0) {
      alert('¡No hay registros para exportar! ¿Qué esperas para empezar a registrar?');
      return;
    }

    const filename = `historial_reproductivo_${new Date().toISOString().split('T')[0]}`;
    const farmName = currentUser?.farmName || 'Mi Finca Ganadera';
    const adminName = currentUser?.fullName || 'Administrador';

    if (format === 'csv') {
      exportToCsv(reproductiveRecords, filename);
      alert('¡Exportando a CSV! Tu historial estará listo en un santiamén.');
    } else if (format === 'pdf') {
      await exportTableToPdf(historyTableRef.current, filename, 'Historial Reproductivo', farmName, adminName, logoUrl);
      alert('¡Generando PDF! Tu historial se verá de lujo en papel.');
    }
  };

  // Filtrar vacas preñadas para la tabla
  const pregnantCows = animals.filter(animal => 
    animal.sexo === 'hembra' && animal.estadoReproductivo === 'preñada'
  ).map(animal => {
    // Encontrar el último registro de gestación positiva para obtener la fecha de parto estimada
    const lastPositiveDiagnosis = reproductiveRecords
      .filter(rec => rec.idAnimal === animal.id && rec.tipoEvento === 'diagnóstico' && rec.estadoPosterior === 'preñada')
      .sort((a, b) => new Date(b.fechaEvento) - new Date(a.fechaEvento))[0];

    const lastMontaInseminacion = reproductiveRecords
      .filter(rec => rec.idAnimal === animal.id && (rec.tipoEvento === 'monta' || rec.tipoEvento === 'inseminacion'))
      .sort((a, b) => new Date(b.fechaEvento) - new Date(a.fechaEvento))[0];

    let fechaOrigenGestacion = '';
    let fechaPartoEstimada = '';

    if (lastPositiveDiagnosis && lastPositiveDiagnosis.fechaPartoEstimada) {
      fechaOrigenGestacion = lastPositiveDiagnosis.fechaEvento;
      fechaPartoEstimada = lastPositiveDiagnosis.fechaPartoEstimada;
    } else if (lastMontaInseminacion) {
      fechaOrigenGestacion = lastMontaInseminacion.fechaEvento;
      const origenDateObj = new Date(fechaOrigenGestacion);
      if (origenDateObj instanceof Date && !isNaN(origenDateObj.getTime())) {
        const estimatedDate = new Date(origenDateObj);
        estimatedDate.setDate(estimatedDate.getDate() + GESTATION_DAYS);
        fechaPartoEstimada = estimatedDate.toISOString().split('T')[0];
      }
    }

    return {
      ...animal,
      fechaOrigenGestacion: fechaOrigenGestacion,
      fechaPartoEstimada: fechaPartoEstimada,
    };
  }).filter(animal => animal.fechaPartoEstimada); // Solo incluir vacas con fecha de parto estimada

  const filteredReproductiveRecords = reproductiveRecords.filter(record => {
    const recordDate = new Date(record.fechaEvento);
    const start = filterStartDate ? new Date(filterStartDate) : null;
    const end = filterEndDate ? new Date(filterEndDate) : null;

    if (start && recordDate < start) return false;
    if (end && recordDate > end) return false;
    return true;
  });

  const maleAnimals = animals.filter(a => a.sexo === 'macho');

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-xl font-semibold mb-4">Control Reproductivo</h3>

      <div className="flex justify-center mb-6 space-x-4">
        <button
          onClick={() => setCurrentSection('montaNatural')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'montaNatural' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Monta Natural
        </button>
        <button
          onClick={() => setCurrentSection('inseminacion')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'inseminacion' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Inseminación
        </button>
        <button
          onClick={() => setCurrentSection('gestacion')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'gestacion' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Control de Gestación / Parto
        </button>
        <button
          onClick={() => setCurrentSection('historial')}
          className={`px-6 py-2 rounded-lg ${currentSection === 'historial' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-800'} hover:bg-blue-700 transition-colors`}
        >
          Historial Reproductivo
        </button>
      </div>

      {currentSection === 'montaNatural' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Registro de Monta Natural</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <label className="block text-gray-700 text-sm font-bold mb-1">Vaca:</label>
            <select
              name="vacaId"
              className="w-full px-4 py-2 border rounded-lg"
              value={montaNaturalData.vacaId}
              onChange={handleMontaNaturalChange}
            >
              <option value="">Selecciona Vaca</option>
              {animals.filter(a => a.sexo === 'hembra').map(vaca => (
                <option key={vaca.id} value={vaca.id}>{vaca.name} ({vaca.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">Toro:</label>
            <select
              name="toroId"
              className="w-full px-4 py-2 border rounded-lg"
              value={montaNaturalData.toroId}
              onChange={handleMontaNaturalChange}
            >
              <option value="">Selecciona Toro</option>
              {animals.filter(a => a.sexo === 'macho').map(toro => (
                <option key={toro.id} value={toro.id}>{toro.name} ({toro.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Monta:</label>
            <input
              type="date"
              name="fechaMonta"
              placeholder="Fecha de Monta"
              className="w-full px-4 py-2 border rounded-lg"
              value={montaNaturalData.fechaMonta}
              onChange={handleMontaNaturalChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones:</label>
            <textarea
              name="observaciones"
              placeholder="Observaciones"
              rows="2"
              className="w-full px-4 py-2 border rounded-lg col-span-full"
              value={montaNaturalData.observaciones}
              onChange={handleMontaNaturalChange}
            ></textarea>
          </div>
          <button
            onClick={handleMontaNaturalSubmit}
            className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
          >
            Registrar Monta
          </button>
        </div>
      )}

      {currentSection === 'inseminacion' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Registro de Inseminación</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <label className="block text-gray-700 text-sm font-bold mb-1">Vaca:</label>
            <select
              name="vacaId"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.vacaId}
              onChange={handleInseminacionChange}
            >
              <option value="">Selecciona Vaca</option>
              {animals.filter(a => a.sexo === 'hembra').map(vaca => (
                <option key={vaca.id} value={vaca.id}>{vaca.name} ({vaca.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">ID Semen Utilizado:</label>
            <input
              type="text"
              name="semenId"
              placeholder="ID Semen Utilizado"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.semenId}
              onChange={handleInseminacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Inseminación:</label>
            <input
              type="date"
              name="fechaInseminacion"
              placeholder="Fecha de Inseminación"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.fechaInseminacion}
              onChange={handleInseminacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1">Inseminador:</label>
            <input
              type="text"
              name="inseminador"
              placeholder="Inseminador (Opcional)"
              className="w-full px-4 py-2 border rounded-lg"
              value={inseminacionData.inseminador}
              onChange={handleInseminacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones:</label>
            <textarea
              name="observaciones"
              placeholder="Observaciones"
              rows="2"
              className="w-full px-4 py-2 border rounded-lg col-span-full"
              value={inseminacionData.observaciones}
              onChange={handleInseminacionChange}
            ></textarea>
          </div>
          <button
            onClick={handleInseminacionSubmit}
            className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
          >
            Registrar Inseminación
          </button>
        </div>
      )}

      {currentSection === 'gestacion' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Control de Gestación / Parto</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <label className="block text-gray-700 text-sm font-bold mb-1">Vaca:</label>
            <select
              name="vacaId"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.vacaId}
              onChange={handleGestacionChange}
            >
              <option value="">Selecciona Vaca</option>
              {animals.filter(a => a.sexo === 'hembra').map(vaca => (
                <option key={vaca.id} value={vaca.id}>{vaca.name} ({vaca.id})</option>
              ))}
            </select>
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha Diagnóstico Gestación:</label>
            <input
              type="date"
              name="fechaDiagnostico"
              placeholder="Fecha de Diagnóstico de Gestación"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.fechaDiagnostico}
              onChange={handleGestacionChange}
            />
            <label className="block text-gray-700 text-sm font-bold mb-1">Resultado Diagnóstico:</label>
            <select
              name="resultadoDiagnostico"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.resultadoDiagnostico}
              onChange={handleGestacionChange}
            >
              <option value="">Resultado Diagnóstico</option>
              <option value="positiva">Positiva (Preñada)</option>
              <option value="negativa">Negativa</option>
            </select>
            {gestacionData.resultadoDiagnostico === 'positiva' && (
              <>
                <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Parto Estimada:</label>
                <input
                  type="date"
                  name="fechaPartoEstimada"
                  placeholder="Fecha de Parto Estimada"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.fechaPartoEstimada}
                  onChange={handleGestacionChange}
                  title="Calculada automáticamente al registrar monta/inseminación o diagnóstico positivo"
                />
              </>
            )}
            <label className="block text-gray-700 text-sm font-bold mb-1">Fecha de Parto Real (si ya ocurrió):</label>
            <input
              type="date"
              name="fechaPartoReal"
              placeholder="Fecha de Parto Real (si ya ocurrió)"
              className="w-full px-4 py-2 border rounded-lg"
              value={gestacionData.fechaPartoReal}
              onChange={handleGestacionChange}
            />
            {gestacionData.fechaPartoReal && (
              <>
                <label className="block text-gray-700 text-sm font-bold mb-1">Peso del Ternero (kg):</label>
                <input
                  type="number"
                  name="pesoTernero"
                  placeholder="Peso del Ternero (kg)"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.pesoTernero}
                  onChange={handleGestacionChange}
                />
                <label className="block text-gray-700 text-sm font-bold mb-1">Sexo del Ternero:</label>
                <select
                  name="sexoTernero"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.sexoTernero}
                  onChange={handleGestacionChange}
                >
                  <option value="">Sexo del Ternero</option>
                  <option value="macho">Macho</option>
                  <option value="hembra">Hembra</option>
                </select>
                <label className="block text-gray-700 text-sm font-bold mb-1">Nombre del Ternero:</label>
                <input
                  type="text"
                  name="nombreTernero"
                  placeholder="Nombre del Ternero"
                  className="w-full px-4 py-2 border rounded-lg"
                  value={gestacionData.nombreTernero}
                  onChange={handleGestacionChange}
                />

                <div className="col-span-full flex items-center space-x-2">
                  <input
                    type="checkbox"
                    id="isPadreRegistered"
                    name="isPadreRegistered"
                    checked={gestacionData.isPadreRegistered}
                    onChange={handleGestacionChange}
                    className="form-checkbox h-4 w-4 text-blue-600"
                  />
                  <label htmlFor="isPadreRegistered" className="text-gray-700 text-sm font-bold">Padre registrado en la finca</label>
                </div>

                {gestacionData.isPadreRegistered ? (
                  <>
                    <label className="block text-gray-700 text-sm font-bold mb-1">Seleccionar Padre:</label>
                    <select
                      name="padreTerneroId"
                      className="w-full px-4 py-2 border rounded-lg"
                      value={gestacionData.padreTerneroId}
                      onChange={handleGestacionChange}
                    >
                      <option value="">Selecciona Padre</option>
                      {maleAnimals.map(toro => (
                        <option key={toro.id} value={toro.id}>{toro.name} ({toro.id})</option>
                      ))}
                    </select>
                  </>
                ) : (
                  <>
                    <label className="block text-gray-700 text-sm font-bold mb-1">Nombre del Padre del Ternero:</label>
                    <input
                      type="text"
                      name="padreTerneroNombre"
                      placeholder="Nombre del Padre del Ternero"
                      className="w-full px-4 py-2 border rounded-lg"
                      value={gestacionData.padreTerneroNombre}
                      onChange={handleGestacionChange}
                    />
                    <label className="block text-gray-700 text-sm font-bold mb-1">Raza del Padre del Ternero:</label>
                    <input
                      type="text"
                      name="razaPadreTernero"
                      placeholder="Raza del Padre del Ternero"
                      className="w-full px-4 py-2 border rounded-lg"
                      value={gestacionData.razaPadreTernero}
                      onChange={handleGestacionChange}
                    />
                  </>
                )}
              </>
            )}
            <label className="block text-gray-700 text-sm font-bold mb-1 col-span-full">Observaciones del Parto:</label>
            <textarea
              name="observacionesParto"
              placeholder="Observaciones del Parto"
              rows="2"
              className="w-full px-4 py-2 border rounded-lg col-span-full"
              value={gestacionData.observacionesParto}
              onChange={handleGestacionChange}
            ></textarea>
          </div>
          <button
            onClick={handleGestacionSubmit}
            className="mt-4 bg-black text-white py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
          >
            Registrar Gestación / Parto
          </button>
        </div>
      )}

      {currentSection === 'historial' && (
        <div className="p-4 border rounded-lg bg-gray-50">
          <h4 className="text-lg font-semibold mb-3">Historial Reproductivo</h4>
          <div className="flex flex-wrap justify-between items-center mb-4 gap-4">
            <button
              onClick={handleAddManualEvent}
              className="bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors"
            >
              Registrar Evento Manual
            </button>
            <div className="flex items-center space-x-2">
              <label className="text-gray-700 text-sm font-bold">Desde:</label>
              <input
                type="date"
                value={filterStartDate}
                onChange={(e) => setFilterStartDate(e.target.value)}
                className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
              />
              <label className="text-gray-700 text-sm font-bold">Hasta:</label>
              <input
                type="date"
                value={filterEndDate}
                onChange={(e) => setFilterEndDate(e.target.value)}
                className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
              />
            </div>
            <div>
              <button
                onClick={() => handleExportHistory('csv')}
                className="bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-colors mr-2"
              >
                Exportar a CSV
              </button>
              <button
                onClick={() => handleExportHistory('pdf')}
                className="bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700 transition-colors"
              >
                Exportar a PDF
              </button>
            </div>
          </div>
          <div className="overflow-x-auto rounded-lg shadow-md">
            <table ref={historyTableRef} className="min-w-full bg-white text-sm">
              <thead className="bg-gray-800 text-white">
                <tr>
                  <th className="py-3 px-4 text-left font-semibold rounded-tl-lg">ID Evento</th>
                  <th className="py-3 px-4 text-left font-semibold">ID Animal</th>
                  <th className="py-3 px-4 text-left font-semibold">Tipo Evento</th>
                  <th className="py-3 px-4 text-left font-semibold">Fecha Evento</th>
                  <th className="py-3 px-4 text-left font-semibold">Observaciones</th>
                  <th className="py-3 px-4 text-left font-semibold">Estado Posterior</th>
                  <th className="py-3 px-4 text-left font-semibold rounded-tr-lg">Acciones</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredReproductiveRecords.map((record) => (
                  <tr key={record.id} className="hover:bg-gray-100 transition-colors">
                    <td className="py-3 px-4">{record.id}</td>
                    <td className="py-3 px-4">{record.idAnimal}</td>
                    <td className="py-3 px-4">{record.tipoEvento}</td>
                    <td className="py-3 px-4">{record.fechaEvento}</td>
                    <td className="py-3 px-4">{record.observaciones}</td>
                    <td className="py-3 px-4">{record.estadoPosterior}</td>
                    <td className="py-3 px-4">
                      <button
                        onClick={() => startEditingHistoryRecord(record)}
                        className="text-blue-600 hover:text-blue-800 font-medium mr-2 transition-colors"
                      >
                        Editar
                      </button>
                      <button
                        onClick={() => handleDeleteHistoryRecord(record.id)}
                        className="text-red-600 hover:text-red-800 font-medium transition-colors"
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Edit Form for History Record */}
          {editingRecord && (
            <div className="mt-8 p-6 border rounded-lg bg-yellow-50 shadow-lg">
              <h4 className="text-lg font-semibold mb-4 text-gray-800">Editar Registro Histórico: {editingRecord.id}</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-gray-700 text-sm font-bold mb-1">Tipo Evento:</label>
                  <input
                    type="text"
                    name="tipoEvento"
                    className="w-full px-4 py-2 border rounded-lg bg-gray-200 cursor-not-allowed"
                    value={editingRecord.tipoEvento}
                    readOnly
                  />
                </div>
                <div>
                  <label className="block text-gray-700 text-sm font-bold mb-1">Fecha Evento:</label>
                  <input
                    type="date"
                    name="fechaEvento"
                    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                    value={editingRecord.fechaEvento}
                    onChange={handleEditingHistoryChange}
                  />
                </div>
                <div>
                  <label className="block text-gray-700 text-sm font-bold mb-1">Estado Posterior:</label>
                  <input
                    type="text"
                    name="estadoPosterior"
                    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                    value={editingRecord.estadoPosterior}
                    onChange={handleEditingHistoryChange}
                  />
                </div>
                <div className="col-span-full">
                  <label className="block text-gray-700 text-sm font-bold mb-1">Observaciones:</label>
                  <textarea
                    name="observaciones"
                    rows="3"
                    className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-black"
                    value={editingRecord.observaciones}
                    onChange={handleEditingHistoryChange}
                  ></textarea>
                </div>
              </div>
              <div className="mt-6 flex justify-end space-x-3">
                <button
                  onClick={saveEditedHistoryRecord}
                  className="bg-green-600 text-white py-2 px-5 rounded-lg hover:bg-green-700 transition-colors shadow-md"
                >
                  Guardar Cambios
                </button>
                <button
                  onClick={cancelEditingHistoryRecord}
                  className="bg-gray-600 text-white py-2 px-5 rounded-lg hover:bg-gray-700 transition-colors shadow-md"
                >
                  Cancelar Edición
                </button>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Calendario de Gestación y Vacas Preñadas */}
      <div className="mt-8 pt-4 border-t border-gray-200">
        <h4 className="text-lg font-semibold mb-3">Vacas Preñadas y Calendario de Gestación</h4>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="py-2 px-4 border">ID Vaca</th>
                <th className="py-2 px-4 border">Nombre</th>
                <th className="py-2 px-4 border">Fecha Origen Gestación</th>
                <th className="py-2 px-4 border">Fecha Parto Estimada</th>
                <th className="py-2 px-4 border">Días Restantes</th>
                <th className="py-2 px-4 border">Acciones</th>
              </tr>
            </thead>
            <tbody>
              {pregnantCows.map(vaca => {
                const diasRestantes = calcularDiasRestantes(vaca.fechaPartoEstimada);
                const isOverdue = diasRestantes < 0;

                return (
                  <tr key={vaca.id}>
                    <td className="py-2 px-4 border">{vaca.id}</td>
                    <td className="py-2 px-4 border">{vaca.name}</td>
                    <td className="py-2 px-4 border">{vaca.fechaOrigenGestacion || 'N/A'}</td>
                    <td className={`py-2 px-4 border ${isOverdue ? 'text-red-600 font-bold' : ''}`}>
                      {vaca.fechaPartoEstimada || 'N/A'}
                    </td>
                    <td className={`py-2 px-4 border ${isOverdue ? 'text-red-600 font-bold' : ''}`}>
                      {isNaN(diasRestantes) ? 'N/A' : (isOverdue ? `Venció hace ${Math.abs(diasRestantes)} días` : diasRestantes)}
                    </td>
                    <td className="py-2 px-4 border">
                      <button
                        className="text-red-600 hover:underline"
                        onClick={() => onDeletePregnantCow(vaca.id)}
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default ReproductiveControlPage;

